custom_css:
      .title {
        font-weight: 400;
        font-size: 14px;
        line-height: 16.8px;
        color: #637381;
      }

      .content {
        font-weight: 400;
        font-size: 14px;
        line-height: 16.8px;
        color: #212b36;
      }
.owl-stage .item {
padding: 10px;
}
      img {
        vertical-align: middle;
      }

      /* Position the image container (needed to position the left and right arrows) */
      .container {
      }

      /* Hide the images by default */
      .mySlides {
        height: 500px;
        position: relative;
      }

      .mySlides img {
        height: 500px;
      }

      /* Add a pointer when hovering over the thumbnail images */
      .cursor {
        cursor: pointer;
      }

      /* Next & previous buttons */
      .prev,
      .next {
        cursor: pointer;
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        color: white;
        font-weight: 400;
        font-size: 10px;
        border-radius: 100px;
        user-select: none;
        -webkit-user-select: none;
        background-color: #225771;
        width: 36px;
        height: 36px;
        display: flex;
        align-items: center;
        justify-content: center;
        z-index: 9;
      }

      /* Position the "next button" to the right */
      .next {
        right: 0;
      }

      .prev {
        left: 0;
      }

      /* On hover, add a black background color with a little bit see-through */
      .prev:hover,
      .next:hover {
        background-color: rgba(0, 0, 0, 0.8);
      }

      /* Number text (1/3 etc) */
      .numbertext {
        color: #f2f2f2;
        font-size: 12px;
        padding: 8px 12px;
        position: absolute;
        top: 0;
      }

      /* Container for image text */
      .caption-container {
        text-align: center;
        background-color: #222;
        padding: 2px 16px;
        color: white;
      }

      .row {
        display: flex;
      }

      /* Six columns side by side */
      .column {
        display: inline-block;
        margin-right: 5px;
      }

      .column img {
        border-radius: 8px;
      }

      /* Add a transparency effect for thumnbail images */
      .demo {
        opacity: 0.6;
      }

      .activeImage,
      .demo:hover {
        opacity: 1;
        border: 3px solid #225771;
      }

      .checked {
        color: orange;
      }

      .arrow-button {
        width: 40px;
        height: 40px;
        background-color: #000;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
      }

      .arrow-icon {
        color: #fff;
        font-size: 30px;
      }
      .floating-label {
        position: relative;
      }
      .floating-label input:focus + label,
      .floating-label input:not(:placeholder-shown) + label {
        transform: translateY(-3em);
        font-size: 0.75rem;
        color: #4a5568; /* Tailwind's gray-700 */
      }

      .floating-label textarea:focus + label,
      .floating-label textarea:not(:placeholder-shown) + label {
        transform: translateY(-1.5em);
        font-size: 0.75rem;
        color: #4a5568; /* Tailwind's gray-700 */
      }

      .floating-label label {
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        left: 8px;
        pointer-events: none;
        transition: all 0.2s ease-out;
        background-color: white;
        padding: 0 0.25em;
      }

      .floating-label .label-textarea {
        top: 10%;
        transform: translateY(-10%);
      }

      /* CSS cho các tab */
      .tab {
        overflow: hidden;
      }

      /* CSS cho các nút bên trong các tab */
      .tab button {
        background-color: white;
        color: #212b36;
        float: left;
        border-radius: 10px;
        outline: none;
        cursor: pointer;
        padding: 14px 16px;
        transition: 0.3s;
      }

      /* Thay đổi màu nền của các nút khi hover */
      .tab button:hover {
        background-color: #5caf90;
        color: white;
      }

      /* Tạo màu nền cho nút hoạt động */
      .tab button.active {
        background-color: #5caf90;
        color: white;
      }

      /* CSS cho nội dung tab */
      .tabcontent {
        display: none;
      }

      /* star rating */

      .rating-container {
        display: flex;
        align-items: center;
      }
      .average-rating {
        margin-right: 20px;
        text-align: center;
      }
      /* .average-rating .stars {
  color: orange;
} */
      .progress-bars {
        width: 200px;
      }
      .progress-bar {
        display: flex;
        align-items: center;
        margin-bottom: 5px;
      }
      .progress-bar span {
        width: 20px;
      }
      .progress {
        width: 100%;
        height: 10px;
        background-color: #e0e0e0;
        border-radius: 5px;
        margin-left: 5px;
        position: relative;
      }
      .progress div {
        height: 100%;
        background-color: #4caf50;
        border-radius: 5px;
        position: absolute;
      }

      /* review */
      .review-container {
        padding: 10px;
        margin: 10px 0;
        border-radius: 5px;
      }
      .review-header {
        display: flex;
        flex-direction: row;
        margin-bottom: 10px;
      }
      .review-header img {
        border-radius: 50%;
        width: 40px;
        height: 40px;
        margin-right: 10px;
      }
      .review-header .name {
        font-weight: bold;
        margin-right: 10px;
      }
      .review-header .stars {
        color: orange;
      }
      .review-body {
        margin-bottom: 10px;
      }
      .review-footer {
        display: flex;
        flex-direction: column;
        font-size: 0.9em;
        color: #777;
      }
      .reply-container {
        padding-left: 10px;
        margin-left: 10px;
        margin-top: 10px;
        display: none;
      }
      .reply-header {
        display: flex;
        align-items: center;
        margin-bottom: 5px;
      }
      .reply-header img {
        border-radius: 50%;
        width: 30px;
        height: 30px;
        margin-right: 10px;
      }
      .reply-header .name {
        font-weight: bold;
        margin-right: 10px;
      }
      .reply-body {
        font-size: 0.9em;
      }
      .show {
        display: block;
      }
