custom_css:
  .text-ellipsis {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-overflow: ellipsis;
  }

.owl-prev {
  height: 36px;
  width: 36px;
  position: absolute;
  top: 50%;
  left: -0.5%;
  transform: translateY(-50%);
  background-color: #6B090B !important;
  color: white !important;
  border-radius: 100px;
}

.owl-prev span, .owl-next span {
  font-size: 20px;
}

.owl-next {
  height: 36px;
  width: 36px;
  position: absolute;
  top: 50%;
  right: -0.5%;
  transform: translateY(-50%);
  background-color: #6B090B !important;
  color: white !important;
  border-radius: 100px;
}

.disabled {
  opacity: 48% !important;
}

custom_js:
$(".owl-carousel").owlCarousel({
    margin: 38,
    responsiveClass: true,
    dots: false,
    responsive: {
      0: {
        items: 1,
        nav: true,
      },
      600: {
        items: 2,
        nav: false,
      },
      1000: {
        items: 3,
        nav: true,
        loop: false,
      },
    },
  });
