      }

custom_js:
const jobContainer = document.getElementById("jobs-container");
const apiGetAllJob =
"/api/method/go1_cms.api.website.jobs.get_all_job";
const findMore = document.getElementById("button-find-more");
const name_section = "{{ name_section }}";

const getAllJob = () => {
  fetch(`${apiGetAllJob}?name_section=${name_section}`, {
    method: "GET",
  })
    .then((res) => res.json())
    .then((result) => {
      console.log(result);
      renderListJob(result.message.data);
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Call api recent news");
    });
};

const renderListJob = (Jobs) => {
  jobContainer.innerHTML = "";
  Jobs?.slice(0, 6).forEach((job) => {
    const jobElement = document.createElement("div");
    jobElement.className =
      "text-left w-full p-6 bg-white border border-gray-200 rounded-lg shadow";
    jobElement.innerHTML = `
              <div class="flex item-center justify-between my-3">
                <p
                  class="text-xs py-1 px-5 font-medium text-center text-black border border-gray-500 rounded-lg"
                >
                  ${job.designation}
                </p>
                <div class="text-gray-500">2h ago</div>
              </div>
              <div class="mt-5">
                <a href="${job.route}">
                  <h5
                    class="mb-2 text-2xl font-bold tracking-tight text-black truncate"
                  >
                    ${job.job_title}
                  </h5>
                </a>
              </div>
              <div class="flex items-center gap-2">
                <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">
                  <i class="fa fa-map-marker"></i> ${job.location}
                </p>
                <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">
                  <i class="fa fa fa-clock-o"></i> ${job.employment_type}
                </p>
              </div>
              <a
                href="${job.route}"
                class="mt-5 w-[109px] h-[40px] inline-flex items-center justify-center px-3 py-2 text-sm font-medium text-[#1877F2] bg-[#919EAB1F] rounded-lg hover:bg-blue-800 hover:text-white focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
              >
                Apply
              </a>
            </div>
          `;

    jobContainer.appendChild(jobElement);
  });
};

findMore.addEventListener("click", () => {
  window.location.href = "/cong-viec";
});

getAllJob();
