custom_css:
.text-container {
      display: -webkit-box;
      -webkit-box-orient: vertical;
      -webkit-line-clamp: 2;
      overflow: hidden;
    }

    .text-line {
      display: -webkit-box;
      -webkit-box-orient: vertical;
      -webkit-line-clamp: 1;
      overflow: hidden;
    }

    .page-number.active {
      border: 1px solid #c4161c;
      color: #c4161c;
      border-radius: 2px;
      font-weight: 500;
    }

    .no-active {
      color: gray;
      pointer-events: none;
    }

custom_js:
let news = [];
let recent_news = [];
const apiBlogList =
  "/api/method/go1_cms.api.website.blog.get_blog_list";
const apiRecentNews =
  "/api/method/go1_cms.api.website.blog.get_recent_blog";
const name_section = "{{ name_section }}";
let text_search = "";
let itemsPerPage = 9; // Số lượng công việc trên mỗi trang
let total_page = 1;
let total = 1;

// Lấy URL hiện tại
const currentUrl = new URL(window.location.href);
const newSearch = document.getElementById("news-search");

const sortBy = document.getElementById("sort-by");

const restoreValuesFromURL = () => {
  const searchParams = new URLSearchParams(window.location.search);
  const sort_by_current = searchParams.get("sort_by");

  if (sort_by_current) {
    sortBy.value = sort_by_current;
  }
};
restoreValuesFromURL();
// Lấy số trang hiện tại từ URL hoặc mặc định là 1
let currentPage = parseInt(currentUrl.searchParams.get("page_number")) || 1;
newSearch.value = currentUrl.searchParams.get("search");

const getRecentBlog = () => {
  fetch(`${apiRecentNews}?name_section=${name_section}`, {
    method: "GET"
  })
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.message) {
        recent_news = result.message?.data;
      }
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Call api recent news");
      displayBlogRecent();
    });
};

const getBlogList = () => {
  const currentUrl = new URL(window.location.href);
  fetch(
    `${apiBlogList}?name_section=${name_section}&text_search=${
      currentUrl.searchParams.get("search") || ""
    }&page_no=${currentPage}&sort_by=${
      currentUrl.searchParams.get("sort_by") || sortBy.value
    }
    &blog_category=Dự án`,
    {
      method: "GET"
    }
  )
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.message) {
        news = result.message?.data;
        itemsPerPage = result.message?.pagination.limit;
        total_page = result.message?.pagination.total_page;
        total = result.message?.pagination.total;
      }
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Call API list new");
      displayNews();
      updateURL();
    });
};

function displayNews() {
  const newContainer = document.getElementById("news-container");
  newContainer.innerHTML = ""; // Xóa nội dung cũ
  if (news.length === 0) {
    const emptyElement = document.createElement("div");
    emptyElement.className =
      "flex flex-col justify-center items-center gap-[38px] py-[80px] col-span-full mt-[38px]";
    emptyElement.innerHTML = `
    <img src="/files/empty-state.svg" alt="" class="w-[245.64px] h-[236.85px]"></img>
    <span class="text-[#212B36] font-bold text-xl">Không có kết quả tìm kiếm </span>
    `;
    newContainer.appendChild(emptyElement);
  }

  for (const blog of news) {
    const blogElement = document.createElement("a");
    blogElement.href = `/${blog.route}`;
    blogElement.className =
      "w-full h-full item bg-white border border-gray-200 rounded-lg shadow";
    blogElement.innerHTML = `
            <img
                class="rounded-t-lg w-full"
                src="https://img.freepik.com/free-photo/painting-mountain-lake-with-mountain-background_188544-9126.jpg"
                alt=""
            />

                <div class="p-5">
                    <p class="text-gray-500 font-normal text-sm leading-[21px] flex gap-2">
                      <svg width="19" height="19" viewBox="0 0 19 19" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M9.5 0.5C8.25 0.5 7.08594 0.742188 6.00781 1.22656C4.91406 1.69531 3.95703 2.33594 3.13672 3.14844C2.31641 3.96094 1.67188 4.91406 1.20312 6.00781C0.734375 7.10156 0.5 8.26562 0.5 9.5C0.5 10.75 0.734375 11.9219 1.20312 13.0156C1.67188 14.1094 2.31641 15.0625 3.13672 15.875C3.95703 16.6875 4.91406 17.3281 6.00781 17.7969C7.08594 18.2656 8.25 18.5 9.5 18.5C10.75 18.5 11.9141 18.2656 12.9922 17.7969C14.0859 17.3281 15.043 16.6875 15.8633 15.875C16.6836 15.0625 17.3281 14.1094 17.7969 13.0156C18.2656 11.9219 18.5 10.75 18.5 9.5C18.5 8.26562 18.2656 7.10156 17.7969 6.00781C17.3281 4.91406 16.6836 3.96094 15.8633 3.14844C15.043 2.33594 14.0859 1.69531 12.9922 1.22656C11.9141 0.742188 10.75 0.5 9.5 0.5ZM13.5078 11.0703C13.4297 11.2578 13.3086 11.4102 13.1445 11.5273C12.9805 11.6445 12.7969 11.7031 12.5938 11.7031C12.5312 11.7031 12.4648 11.6953 12.3945 11.6797C12.3242 11.6641 12.2578 11.6484 12.1953 11.6328H12.2188L9.03125 10.3203C8.84375 10.2422 8.69141 10.1211 8.57422 9.95703C8.45703 9.79297 8.39844 9.60938 8.39844 9.40625V4.69531C8.39844 4.42969 8.49609 4.19922 8.69141 4.00391C8.88672 3.80859 9.125 3.71094 9.40625 3.71094C9.6875 3.71094 9.92578 3.80859 10.1211 4.00391C10.3164 4.19922 10.4141 4.42969 10.4141 4.69531V8.72656L12.9688 9.78125C13.1562 9.85938 13.3086 9.98047 13.4258 10.1445C13.543 10.3086 13.6016 10.4922 13.6016 10.6953C13.6016 10.7734 13.5938 10.8438 13.5781 10.9062C13.5625 10.9688 13.5391 11.0312 13.5078 11.0938V11.0703Z" fill="#637381"/>
                      </svg>
                      ${blog.published_on}
                    </p>

                    <div>
                      <h5
                        class="mb-2 text-lg font-bold tracking-tight text-[#212B36]"
                      >
                        ${blog.title}
                      </h5>
                    </div>
                </div>
        `;
    newContainer.appendChild(blogElement);
  }

  // Hiển thị các nút phân trang
  displayPagination();
  window.scrollTo(0, 0);
}

function displayBlogRecent() {
  const blogRecent = document.getElementById("blog-recent-container");

  for (const blog of recent_news) {
    const blogElement = document.createElement("a");
    blogElement.href = `/${blog.route}`;
    blogElement.className = "flex flex-row item-center w-full my-6 text-line";
    blogElement.innerHTML = `
          <img
            class="h-[50px] w-[63px] rounded-lg"
            alt=""
            src="https://img.freepik.com/free-photo/painting-mountain-lake-with-mountain-background_188544-9126.jpg"
            />
            <div class="mx-3">
                <p
                    class="text-[#637381] font-normal text-xs leading-[18px]"
                >
                    <i class="fa fa-clock-o"></i> ${blog.published_on}
                </p>
                <span
                    class="text-[#212B36] font-semibold text-base leading-5 text-container"
                >${blog.title}
                </span>
            </div>
        `;
    blogRecent.appendChild(blogElement);
  }

  // Hiển thị các nút phân trang
  window.scrollTo(0, 400);
}

function displayPagination() {
  const paginationContainer = document.getElementById("pagination-container");
  total_page <= 1
    ? paginationContainer.classList.add("hidden")
    : paginationContainer.classList.remove("hidden");

  const prevPage = document.getElementById("prev-page");
  const nextPage = document.getElementById("next-page");

  currentPage === 1
    ? prevPage?.classList.add("no-active")
    : prevPage.classList.remove("no-active");
  currentPage === total_page
    ? nextPage?.classList.add("no-active")
    : nextPage.classList.remove("no-active");

  paginationContainer
    ?.querySelectorAll(".page-number")
    ?.forEach((button) => button.remove());

  for (let i = 1; i <= total_page; i++) {
    const pageButton = document.createElement("button");
    pageButton.innerText = i;
    pageButton.className = `page-number text-sm text-[#222222] font-normal px-2 py-[1px] ${
      i === currentPage ? "active" : ""
    }`;
    pageButton.addEventListener("click", () => {
      currentPage = i;
      getBlogList();
    });
    paginationContainer?.insertBefore(
      pageButton,
      document.getElementById("next-page")
    );
  }
}

function updateURL() {
  const currentUrl = new URL(window.location.href);
  currentUrl.searchParams.set("page_number", currentPage);
  currentUrl.searchParams.set("search", newSearch.value);
  currentUrl.searchParams.set("sort_by", sortBy.value);
  history.replaceState({}, "", currentUrl);
}

// Thêm sự kiện click cho nút Previous
document.getElementById("prev-page").addEventListener("click", () => {
  if (currentPage > 1) {
    currentPage--;
    getBlogList();
  }
});

// Thêm sự kiện click cho nút Next
document.getElementById("next-page").addEventListener("click", () => {
  if (currentPage < total_page) {
    currentPage++;
    getBlogList();
  }
});

// Gọi hàm hiển thị lần đầu tiên
getBlogList();
getRecentBlog();

// Debounce function: kỹ thuật debounced
function debounce(func, wait) {
  let timeout;
  return function (...args) {
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(this, args), wait);
  };
}

newSearch.addEventListener(
  "input",
  debounce(() => {
    text_search = newSearch.value;
    currentPage = 1;
    getBlogList();
  }, 1000)
);

newSearch.addEventListener("input", () => {
  updateURL();
});

sortBy.addEventListener("change", () => {
  getBlogList();
});
