function changePostView(view) {
    $(".btn_post_view").removeClass("text-white bg-[#2A2A2C]");
    $(".btn_post_view").addClass("text-[#637381] bg-gray-100");
    $(`.btn_post_view[data-view="${view}"]`).removeClass(
      "text-[#637381] bg-gray-100"
    );
    $(`.btn_post_view[data-view="${view}"]`).addClass(
      "text-white bg-[#2A2A2C]"
    );

    if (view == "grid_view") {
      // remove css list view
      $(".list_item_p").removeClass("line-clamp-5");
      $(".list_item").removeClass("md:flex");
      $(".list_item_img").removeClass(
        "md:min-w-[300px] md:w-[300px] rounded-lg mt-4"
      );

      // add css grid view
      $(".list_post").addClass("md:grid-cols-2 xl:grid-cols-3");
      $(".list_item").addClass("border border-gray-200 rounded-xl");
      $(".list_item_p").addClass("line-clamp-3");
      $(".list_tem_btn").addClass("hidden");
      $(".list_item_img").addClass("rounded-t-xl");
    } else {
      // remove css grid view
      $(".list_post").removeClass("md:grid-cols-2 xl:grid-cols-3");
      $(".list_item").removeClass("border border-gray-200 rounded-xl");
      $(".list_item_p").removeClass("line-clamp-3");
      $(".list_tem_btn").removeClass("hidden");
      $(".list_item_img").removeClass("rounded-t-xl");

      // add css list view
      $(".list_item_p").addClass("line-clamp-5");
      $(".list_item").addClass("md:flex");
      $(".list_item_img").addClass(
        "md:min-w-[300px] md:w-[300px] rounded-lg mt-4"
      );
    }
    blog_view = view;
    setQueryParameter("blog_view", view);
  }
  function pageChangeActive(currentPage, totalPage) {
    // arrow left
    setQueryParameter("page", currentPage);
    if (currentPage > 1) {
      $(".pagination__arrow__left").addClass("active");
      $(".pagination__arrow__left__start").addClass("active");
    } else {
      $(".pagination__arrow__left").removeClass("active");
      $(".pagination__arrow__left__start").removeClass("active");
    }
    // arrow right
    if (currentPage < totalPage) {
      $(".pagination__arrow__right").addClass("active");
      $(".pagination__arrow__right__end").addClass("active");
    } else {
      $(".pagination__arrow__right").removeClass("active");
      $(".pagination__arrow__right__end").removeClass("active");
    }
    // page
    $(".pagination__page").removeClass("active");
    $(`.pagination__page[data-page="${currentPage}"]`).addClass("active");
  }

  function getItemPage(page_no, text_search = "") {
    $.ajax({
      type: "POST",
      Accept: "application/json",
      ContentType: "application/json;charset=utf-8",
      url:
        window.location.origin +
        "/api/method/go1_cms.go1_cms.doctype.web_page_builder.web_page_builder.get_blog_list",
      data: {
        name_section: "{{ name_section }}",
        page_no: page_no,
        text_search: text_search,
      },
      dataType: "json",
      headers: {
        "X-Frappe-CSRF-Token": "{{csrf_token}}",
      },
      success: function (data) {
        let blogs = data.message?.blogs;
        let recent_blogs = data.message?.recent_blogs;

        // load blogs
        if (blogs && blogs.length) {
          totalPage = data.message?.total_page || 0;
          // render blog data
          let html = "";
          $(blogs).each(function (k, v) {
            html += `
              <div class="list_item border border-gray-200 rounded-xl">
                <div class="mb-4">
                  <div>
                    <a
                      class="hover:opacity-85 transition duration-150 ease-in-out"
                      href="/${v.route}"
                    >
                      <img
                        class="list_item_img h-[240px] w-full rounded-t-xl"
                        src="${v.meta_image}"
                        alt=""
                      />
                    </a>
                  </div>
                </div>
                <div class="px-4">
                  <div
                    class="flex items-center text-gray-500 font-medium text-sm"
                  >
                    <img
                      class="h-3 w-3 mr-2"
                      src="/assets/go1_cms/images/Vectorhour.svg"
                      alt=""
                    /><span>${v.published_on}</span>
                  </div>
                  <div class="my-4">
                    <div class="mb-2">
                      <a
                        class="hover:opacity-85 transition duration-150 ease-in-out"
                        href="/${v.route}"
                      >
                        <p
                          title="${v.title}"
                          class="line-clamp-2 text-2xl font-bold"
                        >
                        ${v.title}
                        </p></a
                      >
                    </div>
                    <p
                      class="list_item_p text-base line-clamp-3 text-gray-500"
                    >
                      ${v.blog_intro}
                    </p>
                  </div>
                  <div class="list_tem_btn mb-4 hidden">
                    <a
                      href="/${v.route}"
                      class="inline-block px-4 py-3 bg-gray-100 rounded-lg hover:bg-gray-200 text-black text-base"
                      ><div class="flex items-center text-black">
                        Xem chi tiết<img
                          class="h-4 w-4 ml-2"
                          src="/assets/go1_cms/images/arrow_right.svg"
                          alt=""
                        /></div
                    ></a>
                  </div>
                </div>
              </div>
              `;
          });
          $("#blog-list").html(html);

          if (totalPage) {
            let startPage = 1;
            let endPage = totalPage;
            let step = 2;
            let p_blog = `
                <ul class="pagination flex items-center gap-1 text-lg">
                  <li>
                    <button
                      class="pagination__arrow__left__start cursor-not-allowed px-3 py-1 rounded-md text-gray-500"
                    >
                    <svg class="h-4" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" fill="currentColor"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path fill="currentColor" d="M8.39727345,2.21077392 C8.67341324,1.93425491 9.12633841,1.92915248 9.40891014,2.19937732 C9.69148187,2.46960216 9.69669599,2.91282562 9.4205562,3.18934463 L9.4205562,3.18934463 L2.72165154,9.89744874 L9.79010593,16.8045692 C10.0696807,17.0777629 10.070006,17.5210156 9.7908324,17.7946019 C9.51165882,18.0681882 9.05870375,18.0685065 8.77912894,17.7953128 L8.77912894,17.7953128 L1.20989415,10.398842 C0.932450728,10.127731 0.929706681,9.68859574 1.20374127,9.41418482 L1.20374127,9.41418482 Z M17.3972735,2.21077392 C17.6734132,1.93425491 18.1263384,1.92915248 18.4089101,2.19937732 C18.6914819,2.46960216 18.696696,2.91282562 18.4205562,3.18934463 L18.4205562,3.18934463 L11.7216515,9.89744874 L18.7901059,16.8045692 C19.0696807,17.0777629 19.070006,17.5210156 18.7908324,17.7946019 C18.5116588,18.0681882 18.0587037,18.0685065 17.7791289,17.7953128 L17.7791289,17.7953128 L10.2098942,10.398842 C9.93245073,10.127731 9.92970668,9.68859574 10.2037413,9.41418482 L10.2037413,9.41418482 Z"></path> </g></svg>
                    </button>
                  </li>
                  <li>
                    <button
                      class="pagination__arrow__left cursor-not-allowed px-3 py-1 rounded-md text-gray-500"
                    >
                      <svg
                        class="h-4"
                        viewBox="0 0 7 12"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M6.83938 2.06621V1.03094C6.83938 0.941211 6.73625 0.891658 6.66661 0.946568L0.629107 5.66219C0.57781 5.70209 0.536302 5.75317 0.50775 5.81154C0.479198 5.86992 0.464355 5.93404 0.464355 5.99902C0.464355 6.06401 0.479198 6.12813 0.50775 6.18651C0.536302 6.24488 0.57781 6.29596 0.629107 6.33585L6.66661 11.0515C6.73759 11.1064 6.83938 11.0568 6.83938 10.9671V9.93184C6.83938 9.86621 6.80857 9.80327 6.75768 9.76309L1.93625 5.99969L6.75768 2.23496C6.80857 2.19478 6.83938 2.13184 6.83938 2.06621Z"
                          fill="currentColor"
                        />
                      </svg>
                    </button>
                  </li>
                  `;

            if (page - step > 0) {
              startPage = page - step;
            }
            if (page + step <= totalPage) {
              endPage = page + step;
            }
            while (endPage < totalPage && endPage - startPage < step * 2) {
              endPage++;
            }
            while (startPage > 1 && endPage - startPage < step * 2) {
              startPage--;
            }

            for (let index = startPage; index <= endPage; index++) {
              p_blog += `<li>
                      <button
                        data-page="${index}"
                        class="pagination__page cursor-pointer px-3 py-1 rounded-md text-black"
                      >
                        ${index}
                      </button>
                    </li>`;
            }

            p_blog += `
                  <li>
                    <button
                      class="pagination__arrow__right cursor-not-allowed px-3 py-1 rounded-md text-gray-500 active"
                    >
                      <svg
                        class="h-4"
                        viewBox="0 0 7 12"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M6.39777 5.66281L0.360269 0.947189C0.344493 0.934768 0.325533 0.92705 0.305568 0.924919C0.285603 0.922788 0.265441 0.926332 0.247399 0.935143C0.229357 0.943955 0.214166 0.957676 0.203571 0.974732C0.192976 0.991787 0.187406 1.01149 0.187501 1.03156V2.06683C0.187501 2.13246 0.218305 2.1954 0.269198 2.23558L5.09063 6.00031L0.269198 9.76505C0.216966 9.80522 0.187501 9.86817 0.187501 9.9338V10.9691C0.187501 11.0588 0.290626 11.1083 0.360269 11.0534L6.39777 6.33781C6.44908 6.29779 6.4906 6.24658 6.51915 6.1881C6.5477 6.12962 6.56254 6.06539 6.56254 6.00031C6.56254 5.93523 6.5477 5.87101 6.51915 5.81253C6.4906 5.75404 6.44908 5.70284 6.39777 5.66281Z"
                          fill="currentColor"
                        />
                      </svg>
                    </button>
                  </li>
                  <li>
                    <button
                      class="pagination__arrow__right__end cursor-not-allowed px-3 py-1 rounded-md text-gray-500"
                    >
                    <svg class="h-4" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" fill="currentColor"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path fill="currentColor" fill-rule="evenodd" d="M2.54214478,2.15371704 L9.79625873,9.41418482 C9.93208624,9.55447396 10,9.71556193 10,9.89744874 C10,10.0793356 9.93208624,10.2457418 9.79625873,10.3966675 L2.22087106,17.7953128 C1.83778384,18.1121603 1.49706014,18.1121603 1.19869995,17.7953128 C0.900339762,17.4784653 0.900339762,17.151685 1.19869995,16.8149719 L8.27834846,9.89744874 L1.52534485,3.1342926 C1.28786041,2.79147628 1.30977541,2.47983785 1.59108986,2.19937732 C1.8724043,1.91891679 2.18942261,1.9036967 2.54214478,2.15371704 Z M11.5421448,2.15371704 L18.7962587,9.41418482 C18.9320862,9.55447396 19,9.71556193 19,9.89744874 C19,10.0793356 18.9320862,10.2457418 18.7962587,10.3966675 L11.2208711,17.7953128 C10.8377838,18.1121603 10.4970601,18.1121603 10.1987,17.7953128 C9.90033976,17.4784653 9.90033976,17.151685 10.1987,16.8149719 L17.2783485,9.89744874 L10.5253448,3.1342926 C10.2878604,2.79147628 10.3097754,2.47983785 10.5910899,2.19937732 C10.8724043,1.91891679 11.1894226,1.9036967 11.5421448,2.15371704 Z"></path> </g></svg>
                    </button>
                  </li>
                </ul>
                `;

            $("#pagination_blog").html(p_blog);
            $("#pagination_blog").removeClass("hidden");
            // loading pagination
            pageChangeActive(page, totalPage);
          } else {
            if (!$("#pagination_blog").hasClass("hidden")) {
              $("#pagination_blog").addClass("hidden");
            }
          }
        } else {
          let html = `
              <div
                class="col-span-1 md:col-span-2 xl:col-span-3 text-center rounded-lg p-4 bg-gray-100"
              >
                {{ content_empty_left }}
              </div>
            `;
          $("#blog-list").html(html);
          $("#pagination_blog").html("");
          if (!$("#pagination_blog").hasClass("hidden")) {
            $("#pagination_blog").addClass("hidden");
          }
        }

        // load recent blogs
        if (recent_blogs && recent_blogs.length) {
          let html = ``;
          $(recent_blogs).each(function (k, v) {
            html += `
              <div class="flex">
              <div class="mb-4">
                <div>
                  <a
                    class="hover:opacity-85 transition duration-150 ease-in-out"
                    href="/${v.route}"
                  >
                    <img
                      class="h-[50px] min-w-16 w-16 rounded-md"
                      src="${v.meta_image}"
                      alt=""
                    />
                  </a>
                </div>
              </div>
              <div class="px-4">
                <div
                  class="flex items-center text-gray-500 font-medium text-xs"
                >
                  <img
                    class="h-3 w-3 mr-2"
                    src="/assets/go1_cms/images/Vectorhour.svg"
                    alt=""
                  /><span>${v.published_on}</span>
                </div>
                <div class="mt-2">
                  <a
                    class=" hover:opacity-85 transition duration-150 ease-in-out"
                    href="/${v.route}"
                  >
                    <p
                      title="${v.title}"
                      class="line-clamp-1 text-base font-bold"
                    >
                      ${v.title}
                    </p></a
                  >
                </div>
              </div>
            </div>
              `;
          });
          $("#recent_blogs").html(html);
        } else {
          let html = `
              <div
                class="col-span-1 md:col-span-2 xl:col-span-3 text-center rounded-lg p-4 bg-gray-100"
              >
                {{ content_empty_right }}
              </div>
            `;
          $("#recent_blogs").html(html);
        }

        if (!$("#loading_blog_1").hasClass("hidden")) {
          $("#loading_blog_1").addClass("hidden");
        }
        if (!$("#loading_blog_2").hasClass("hidden")) {
          $("#loading_blog_2").addClass("hidden");
        }
        changePostView(blog_view);
        refreshActionPagination();
      },
      error: function (err) {},
    });
  }

  function refreshActionPagination() {
    $(".pagination__arrow__left__start").click(function () {
      if (page != 1) {
        page = 1;
        getItemPage(page);
        pageChangeActive(page, totalPage);
      }
    });
    $(".pagination__arrow__left").click(function () {
      if (page > 1) {
        page--;
        getItemPage(page);
        pageChangeActive(page, totalPage);
      }
    });
    $(".pagination__arrow__right").click(function () {
      if (page < totalPage) {
        page++;
        getItemPage(page);
        pageChangeActive(page, totalPage);
      }
    });
    $(".pagination__arrow__right__end").click(function () {
      if (page != totalPage) {
        page = totalPage;
        getItemPage(page);
        pageChangeActive(page, totalPage);
      }
    });
    $(".pagination__page").click(function () {
      if (page != $(this).data("page")) {
        page = $(this).data("page");
        getItemPage(page);
        pageChangeActive(page, totalPage);
      }
    });
  }

  var page = isNumeric(getQueryParameter("page"))
    ? Number(getQueryParameter("page"))
    : 1;
  var text_search = getQueryParameter("text_search") || "";
  var blog_view = getQueryParameter("blog_view") || "grid_view";
  var totalPage = 0;
  $(document).ready(function () {
    // load data first
    getItemPage(page, text_search);
    changePostView(blog_view);
    if (text_search) {
      $("#text_blog_search").val(text_search);
    }

    $(".btn_post_view").click(function () {
      changePostView($(this).data("view"));
    });

    $("#btn_search_blog").click(function () {
      let val = $("#text_blog_search").val();
      getItemPage(1, val);
      setQueryParameter("text_search", val);
    });
    $("#text_blog_search").on("input", function () {
      let val = $(this).val();
      if (!val) {
        getItemPage(1, "");
        removeQueryParameter("text_search");
      }
    });
  });
