$(document).ready(function () {
    $("#{{ class_name }}_formContact").submit(function (event) {
      event.preventDefault();
      let formData = new FormData(this);
      formData.append("utm_source", getCookie("utm_source") || "");
      formData.append("utm_campaign", getCookie("utm_campaign") || "");
      formData.append("source", getCookie("source") || window.location.href);

      $.ajax({
        type: "POST",
        url: "{{form_data.form_action}}",
        data: formData,
        processData: false,
        contentType: false,
        headers: {
            "X-Frappe-CSRF-Token": "{{csrf_token}}", 
        },
        success: function (response) {
          Swal.fire({
            position: "bottom-end",
            icon: "success",
            title: "<h4>Gửi thông tin thành công</h4>",
            showConfirmButton: false,
            timer: 1500,
          });
          $("#{{ class_name }}_formContact")[0].reset();
        },
        error: function (error) {
          Swal.fire({
            position: "bottom-end",
            icon: "error",
            title: "<h4>Gửi thông tin thất bại</h4>",
            showConfirmButton: false,
            timer: 1500,
          });
        },
      });
    });
  });
