custom_js:
$(document).ready(function () {
    $("#{{ class_name }}_formContact").submit(function (event) {
      event.preventDefault(); // Prevent the form from submitting the traditional way
      const formData = $(this).serialize(); // Serialize the form data
      $.ajax({
        type: "POST",
        url: "{{form_data.form_action}}",
        data: formData,
        success: function (response) {
          Swal.fire({
            position: "bottom-end",
            icon: "success",
            title: "<h4>Gửi thông tin thành công</h4>",
            showConfirmButton: false,
            timer: 1500,
          });
          $("#{{ class_name }}_formContact")[0].reset();
        },
        error: function (error) {
          Swal.fire({
            position: "bottom-end",
            icon: "error",
            title: "<h4>Gửi thông tin thất bại</h4>",
            showConfirmButton: false,
            timer: 1500,
          });
        },
      });
    });
  });

