custom_css:
#ourteam_mbw .owl-stage{display: flex;}
#ourteam_mbw .ourteam_item{
    display: flex;
    flex: 1 0 auto;
    height: 100%;
}
#ourteam_mbw .owl-prev, #ourteam_mbw .owl-next {
margin-right: 10px;
background: gray;
padding: 15px 17px !important;
 border-radius: 50%;
opacity: 0.65;
position: absolute;
top: 50%;
transform: translate(0%, -50%);
}
#ourteam_mbw .owl-prev {
left: -15px;
}
#ourteam_mbw .owl-next{
right: -15px;
}
#ourteam_mbw .owl-prev:hover, #ourteam_mbw .owl-next:hover {
opacity: 1;
background: #212B36;
}
#ourteam_mbw .owl-nav{
display: flex;
margin-top: 25px
}

custom_js:
$('#ourteam_mbw').owlCarousel({
    loop:true,
    margin:10,
    nav:true,
    autoHeight: false,
    navText: [`<img src="/assets/go1_cms/images/primary-shapearrow_left.png">`,`<img src="/assets/go1_cms/images/primary-shapearrow_right.png">`],
    dots: false,
    autoplay:true,
    autoplayTimeout:3000,
    autoplayHoverPause:false,
    responsive:{
        0:{
            items:1
        },
        600:{
            items:2
        },
        1000:{
            items:4
        }
    }
})

var speed = 15;
          var tab = document.getElementById("demo_{{class_name}}");
          var tab1 = document.getElementById("demo1_{{class_name}}");
          var tab2 = document.getElementById("demo2_{{class_name}}");
          tab2.innerHTML = tab1.innerHTML;
          function Marquee() {
            if (tab2.offsetWidth - tab.scrollLeft <= 0)
              tab.scrollLeft = 0;
            else {
              tab.scrollLeft++;
            }
          }
          var MyMar = setInterval(Marquee, speed);
          tab.onmouseover = function () {
            clearInterval(MyMar);
          };
          tab.onmouseout = function () {
            MyMar = setInterval(Marquee, speed);
          };
