let news = [];
      const name_section = "{{ name_section }}";
      const apiBlogList = "/api/method/go1_cms.api.website.blog.get_blog_list";
      const getBlogList = () => {
        fetch(
          `${apiBlogList}?name_section=${name_section}&blog_category=Chăm sóc da`,
          {
            method: "GET"
          }
        )
          .then((response) => response.json())
          .then((result) => {
            console.log(result);
            if (result.message) {
              news = result.message?.data;
            }
          })
          .catch((error) => {
            console.log(error);
          })
          .finally(() => {
            console.log("Call API list new");
            displayNews();
          });
      };

      function displayNews() {
        const newContainer = document.getElementById("news-container");

        for (const blog of news) {
          const blogElement = document.createElement("div");
          blogElement.className = "h-[510px] relative";
          blogElement.innerHTML = `
