              class="absolute bottom-0 left-1/2 -translate-x-1/2 flex flex-col gap-[10px] bg-white shadow-md p-[15px] w-4/5 rounded-lg"
            >
              <div class="flex justify-between">
                <div>
                  <i class="fa fa-clock-o" aria-hidden="true"></i>
                  <span class="font-normal text-sm span-black">${blog.published_on}</span>
                </div>
                <div class="">
                  <i class="fa fa-user" aria-hidden="true"></i>
                  <span class="text-sm font-normal span-black"
                    >${blog.blogger}</span
                  >
                </div>
              </div>
              <span class="font-medium text-[22px] leading-7 span-black"
                >${blog.title}</span
              >
              <a href="/${blog.route}" class="flex border text-[#919EAB52] w-fit p-2 rounded-lg">
