custom_css:
.text-container {
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3; /* Số dòng hiển thị trước khi hiển thị dấu ba chấm */
  overflow: hidden;
  text-overflow: ellipsis;
}

.text-hide {
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1; /* Số dòng hiển thị trước khi hiển thị dấu ba chấm */
  overflow: hidden;
  text-overflow: ellipsis;
}

.active-icon-view .icon path,
.view-icon:hover .icon path {
  fill: white;
}

.active-icon-view {
  background-color: #225771;
}

input[type="checkbox"]:checked + label {
  font-weight: bold;
}

input[type="checkbox"].custom-checkbox {
  appearance: none;
  -webkit-appearance: none;
  width: 16px;
  height: 16px;
  background-color: white;
  border: 2px solid black;
  border-radius: 0; /* Để tạo hình vuông */
  position: relative;
}

input[type="checkbox"].custom-checkbox:checked::before {
  content: "";
  display: block;
  width: 8px;
  height: 8px;
  background-color: black;
  position: absolute;
  top: 2px;
  left: 2px;
}

.page-number.active {
  border: 1px solid #c4161c;
  color: #c4161c;
  border-radius: 4px;
  font-weight: 500;
}

.no-active {
  color: gray;
  pointer-events: none;
}

/* Overlay for the entire screen */
.loading-overlay {
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 9999; /* Ensure it is above all other elements */
  display: none; /* Hidden by default */
}

/* Spinner */
.spinner {
  border: 4px solid rgba(0, 0, 0, 0.1);
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
}

/* Spinner animation */
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.filter-toggle {
  display: none;
  position: fixed;
  right: 0;
  top: 50%;
  transform: translateY(-50%);
  background: #212b36;
  color: white;
  border: none;
  padding: 10px;
  border-radius: 5px;
  cursor: pointer;
  width: 64px;
  height: 48px;
}

.overlay {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  z-index: 999;
}

@media (max-width: 1280px) {
  .filter {
    position: fixed;
    top: 0;
    right: 0;
    height: 100%;
    width: 70%;
    z-index: 1000;
    transform: translateX(-100%);
    transition: transform 0.3s ease;
    overflow-y: auto;
    display: flex;
    justify-content: flex-end;
    background-color: rgba(0, 0, 0, -0.5);
  }

  .display {
    display: none;
  }

  .filter.active {
    transform: translateX(0);
  }

  .filter-toggle {
    display: block;
  }

  .overlay.active {
    display: block;
  }
}

.icon-hover:hover .icon path {
  fill: #5caf90;
} /* Change this color to your desired hover color */

