custom_js:
const counter = document.querySelectorAll("#counter");
     counter.forEach((counter) => {
       const target = counter.getAttribute("data-value");
       if (target > 1000) {
         counter.innerText = "1000+";
       } else {
         counter.innerText = target;
       }
     });

custom_css:
.text-container {
        max-height: 100px;
        overflow: hidden;
        text-overflow: ellipsis;
        display: -webkit-box;
        -webkit-line-clamp: 4;
        -webkit-box-orient: vertical;
      }

.text-container {
       display: -webkit-box;
       -webkit-box-orient: vertical;
       -webkit-line-clamp: 2;
       overflow: hidden;
     }

     .text-line {
       display: -webkit-box;
       -webkit-box-orient: vertical;
       -webkit-line-clamp: 1;
       overflow: hidden;
     }
