custom_css:
.active {
  background-color: rgba(34, 197, 94, 0.08);
  font-weight: 700;
  font-size: 16px;
  line-height: 22px;
  color: #5caf90;
}

.floating-label {
  position: relative;
}
.floating-label input:focus + label,
.floating-label input:not(:placeholder-shown) + label {
  transform: translateY(-3em);
  font-size: 0.75rem;
  color: #4a5568; /* Tailwind's gray-700 */
}

.floating-label textarea:focus + label,
.floating-label textarea:not(:placeholder-shown) + label {
  transform: translateY(-1.5em);
  font-size: 0.75rem;
  color: #4a5568; /* Tailwind's gray-700 */
}

.floating-label label {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: 8px;
  pointer-events: none;
  transition: all 0.2s ease-out;
  background-color: white;
  padding: 0 0.25em;
}

.floating-label .label-textarea {
  top: 10%;
  transform: translateY(-10%);
}

.error {
  color: red;
  font-size: 14px;
}

.floating-label:not(:first-child) {
  margin-top: 1rem; /* mt-4 tương đương với 1rem */
}

.loader {
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 20px;
  height: 20px;
  animation: spin 2s linear infinite;
  cursor: not-allowed;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.button-disabled {
  background-color: #d1d5db !important;
  cursor: not-allowed !important;
}

/* Overlay for the entire screen */
.loading-overlay {
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 9999; /* Ensure it is above all other elements */
  display: none; /* Hidden by default */
}

/* Spinner */
.lds-spinner {
  /* change color here */
  color: #1c4c5b
}
.lds-spinner,
.lds-spinner div,
.lds-spinner div:after {
  box-sizing: border-box;
}
.lds-spinner {
  color: currentColor;
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;
}
.lds-spinner div {
  transform-origin: 40px 40px;
  animation: lds-spinner 1.2s linear infinite;
}
.lds-spinner div:after {
  content: " ";
  display: block;
  position: absolute;
  top: 3.2px;
  left: 36.8px;
  width: 6.4px;
  height: 17.6px;
  border-radius: 20%;
  background: currentColor;
}
.lds-spinner div:nth-child(1) {
  transform: rotate(0deg);
  animation-delay: -1.1s;
}
.lds-spinner div:nth-child(2) {
  transform: rotate(30deg);
  animation-delay: -1s;
}
.lds-spinner div:nth-child(3) {
  transform: rotate(60deg);
  animation-delay: -0.9s;
}
.lds-spinner div:nth-child(4) {
  transform: rotate(90deg);
  animation-delay: -0.8s;
}
.lds-spinner div:nth-child(5) {
  transform: rotate(120deg);
  animation-delay: -0.7s;
}
.lds-spinner div:nth-child(6) {
  transform: rotate(150deg);
  animation-delay: -0.6s;
}
.lds-spinner div:nth-child(7) {
  transform: rotate(180deg);
  animation-delay: -0.5s;
}
.lds-spinner div:nth-child(8) {
  transform: rotate(210deg);
  animation-delay: -0.4s;
}
.lds-spinner div:nth-child(9) {
  transform: rotate(240deg);
  animation-delay: -0.3s;
}
.lds-spinner div:nth-child(10) {
  transform: rotate(270deg);
  animation-delay: -0.2s;
}
.lds-spinner div:nth-child(11) {
  transform: rotate(300deg);
  animation-delay: -0.1s;
}
.lds-spinner div:nth-child(12) {
  transform: rotate(330deg);
  animation-delay: 0s;
}
@keyframes lds-spinner {
  0% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}
