custom_js:
let services = [];
     const name_section = "{{ name_section }}";
     const apiBlogList =
       "/api/method/go1_cms.api.website.blog.get_blog_list";
     let currentPage = 1; // Biến lưu số trang hiện tại
     const blogCategory = "dịch vụ"; // Danh mục blog
     let totalPage = 1; // Tổng số trang

     const getServiceList = (page_no) => {
       fetch(
         `${apiBlogList}?name_section=${name_section}&page_no=${page_no}
             &blog_category=${blogCategory}`,
         {
           method: "GET"
         }
       )
         .then((response) => response.json())
         .then((result) => {
           console.log(result);
           if (result.message) {
             services = result.message?.data;
             totalPage = result.pagination?.total_page;
             document.getElementById("loadMoreBtn").style.display =
               currentPage < totalPage ? "block" : "none";
           }
         })
         .catch((error) => {
           console.log(error);
         })
         .finally(() => {
           console.log("Call API list new");
           displayServices();
         });
     };

     function displayServices() {
       const newContainer = document.getElementById("services-container");

       for (const blog of services) {
         const blogElement = document.createElement("a");
         blogElement.href = `/${blog.route}`;
         blogElement.className =
           "w-full h-full item bg-white border border-gray-200 rounded-lg shadow";
         blogElement.innerHTML = `
                 <img
                     class="rounded-t-lg w-full"
                     src="https://img.freepik.com/free-photo/painting-mountain-lake-with-mountain-background_188544-9126.jpg"
                     alt=""
                 />

                     <div class="p-5">
                         <div class="flex justify-center">
                           <h5
                             class="mb-2 text-lg font-bold tracking-tight text-[#212B36]"
                           >
                             ${blog.title}
                           </h5>
                         </div>
                     </div>
             `;
         newContainer.appendChild(blogElement);
       }
     }

     // Thêm sự kiện cho nút "Xem thêm"
     document.getElementById("loadMoreBtn").addEventListener("click", () => {
       currentPage++; // Tăng số trang
       getServiceList(currentPage); // Gọi API với trang tiếp theo
     });

     getServiceList(currentPage);
