custom_css:
.owl-prev,
.owl-next {
        display: none !important;
}

.owl-dots {
   justify-content: center;
   display: flex;
   gap: 20px;
   margin-top: 38px;
}
.owl-dot {
     width: 10px;
     height: 10px;
     background-color: #fadcd9 !important;
     border-radius: 100px;
}
.owl-dots .active {
   background-color: #f79489 !important;
}

.text-container {
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 4; /* Số dòng hiển thị trước khi hiển thị dấu ba chấm */
    overflow: hidden;
    text-overflow: ellipsis;
  }

custom_js:
$(".owl-carousel").owlCarousel({
          loop: true,
          margin: 38,
          responsiveClass: true,
          nav: false,
          responsive: {
            0: {
              items: 1,
              nav: true,
            },
            700: {
              items: 2,
              nav: false,
            },
            1000: {
              items: 3,
              nav: true,
              loop: false,
            },
          },
        })
lazyLoading();
