custom_css:
.text-container {
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3; /* Số dòng hiển thị trước khi hiển thị dấu ba chấm */
  overflow: hidden;
  text-overflow: ellipsis;
}

.text-hide {
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1; /* Số dòng hiển thị trước khi hiển thị dấu ba chấm */
  overflow: hidden;
  text-overflow: ellipsis;
}

.active-icon-view .icon path,
.view-icon:hover .icon path {
  fill: white;
}

.active-icon-view {
  background-color: #225771;
}

input[type="radio"]:checked + label {
  font-weight: bold;
}

input[type="radio"].custom-radio {
  appearance: none;
  -webkit-appearance: none;
  width: 16px;
  height: 16px;
  background-color: white;
  border: 2px solid #4299e1;
  border-radius: 0; /* Để tạo hình vuông */
  position: relative;
}

input[type="radio"].custom-radio:checked::before {
  content: "";
  display: block;
  width: 8px;
  height: 8px;
  background-color: #4299e1;
  position: absolute;
  top: 2px;
  left: 2px;
}

.loading-overlay {
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 9999; /* Ensure it is above all other elements */
  display: none; /* Hidden by default */
}

.spinner {
  border: 4px solid rgba(0, 0, 0, 0.1);
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
}

/* Spinner animation */
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.page-number.active {
  border: 1px solid #c4161c;
  color: #c4161c;
  border-radius: 2px;
  font-weight: 500;
}

.no-active {
  color: gray;
  pointer-events: none;
}

