document.addEventListener("DOMContentLoaded", function () {
  const filterToggle = document.getElementById("filterToggle");
  const filter = document.getElementById("filter");
  const closeButton = document.getElementById("button-close-filter");
  const overlay = document.getElementById("overlay");

  filterToggle.addEventListener("click", function () {
    filter.classList.add("active");
    filter.classList.remove("display");
    overlay.classList.toggle("active");
  });
  closeButton.addEventListener("click", () => {
    filter.classList.add("display");
    filter.classList.remove("active");
    overlay.classList.remove("active");
  });

  overlay.addEventListener("click", function () {
    filter.classList.remove("active");
    overlay.classList.remove("active");
    filter.classList.add("display");
  });

  let jobs = [];
  let itemsPerPage = 8;
  let currentPage = 1;
  let totalPages = 1;

  const searchParams = new URLSearchParams(window.location.search);

  const filterForm = document.getElementById("formFilter");
  const loadingIndicator = document.getElementById("loading");
  const deleteRangeFrom = document.getElementById("delete-range-from");
  const deleteRangeTo = document.getElementById("delete-range-to");
  const rangeFrom = document.getElementById("range_from");
  const rangeTo = document.getElementById("range_to");
  const resetFilter = document.getElementById("resetFilter");
  const sortAsc = document.getElementById("sort-asc");
  const sortDesc = document.getElementById("sort-desc");
  const job_name = document.getElementById("job-name") || "";
  const jobDesignationContainer = document.getElementById(
    "job_designation_container"
  );

  let sortType = searchParams.get("sort_by") === "asc" ? 1 : 2;
  let changeTimer; // Timer identifier
  let changeInterval = 1000; // Thời gian chờ (ms)
  const name_section = "{{ name_section }}";

  const jobTypes = [];
  const jobLocations = [];
  const jobDesignation = [];

  if (sortType === 1) {
    sortAsc.classList.remove("hidden");
    sortDesc.classList.add("hidden");
  } else {
    sortAsc.classList.add("hidden");
    sortDesc.classList.remove("hidden");
  }

  document.getElementById("job-name").addEventListener("input", function () {
    clearTimeout(changeTimer);
    changeTimer = setTimeout(doneChanging, changeInterval);
  });

  // Hàm xử lý sau khi giá trị input thay đổi
  function doneChanging() {
    disableUI(true);
    showLoading(true);
    handleSearch();
  }

  resetFilter.addEventListener("click", (e) => {
    e.preventDefault();
    const resetDataFilterFormCheckBox = (input, array) => {
      document.querySelectorAll(input)?.forEach((checkbox) => {
        checkbox.checked = false;
      });
      array.length = 0;
      return array;
    };

    resetDataFilterFormCheckBox('input[name="job-type"]', jobTypes);
    resetDataFilterFormCheckBox('input[name="job-locations"]', jobLocations);
    resetDataFilterFormCheckBox(
      'input[name="job_designation"]',
      jobDesignation
    );
    rangeFrom.value = "";
    rangeTo.value = "";
    job_name.value = "";
    sortType = 1;

    rangeSalary = [null, null];

    const data = {
      text_search: job_name.value || "",
      job_type: jobTypes || [],
      job_location: jobLocations || [],
      job_designation: jobDesignation || [],
      range_salary: rangeSalary,
      sort_by: sortType === 1 ? "asc" : "desc",
    };

    updateUrl(data);
    setTimeout(() => {
      handleSearch();
    }, 0);
  });

  const updateUrl = (data) => {
    const params = new URLSearchParams();
    // Duyệt qua các thuộc tính của object `data` và thêm chúng vào params
    Object.keys(data).forEach((key) => {
      if (Array.isArray(data[key])) {
        // Nếu là mảng, thì thêm từng giá trị của mảng vào params với cùng một key
        data[key].forEach((value) => params.append(key, value));
      } else {
        // Nếu không phải mảng, thêm trực tiếp vào params
        params.set(key, data[key]);
      }
    });

    const newUrl = `${window.location.pathname}?${params.toString()}`;
    history.replaceState(null, "", newUrl);
  };

  deleteRangeFrom.addEventListener("click", () => {
    rangeFrom.value = "";
    rangeSalary =
      rangeFrom.value !== "" || rangeTo.value !== ""
        ? [
            parseInt(document.getElementById("range_from").value) || null,
            parseInt(document.getElementById("range_to").value) || null,
          ]
        : [null, null];
    const data = {
      text_search: job_name.value || "",
      job_type: jobTypes || [],
      job_location: jobLocations || [],
      job_designation: jobDesignation || [],
      range_salary: rangeSalary,
      sort_by: sortType === 1 ? "asc" : "desc",
    };
    updateUrl(data);
    handleSearch();
  });

  deleteRangeTo.addEventListener("click", () => {
    rangeTo.value = "";
    rangeSalary =
      rangeFrom.value !== "" || rangeTo.value !== ""
        ? [
            parseInt(document.getElementById("range_from").value) || null,
            parseInt(document.getElementById("range_to").value) || null,
          ]
        : [null, null];
    const data = {
      text_search: job_name.value || "",
      job_type: jobTypes || [],
      job_location: jobLocations || [],
      job_designation: jobDesignation || [],
      range_salary: rangeSalary,
      sort_by: sortType === 1 ? "asc" : "desc",
    };
    updateUrl(data);
  });

  filterForm.addEventListener("change", (e) => {
    e.preventDefault();
    doneChanging();
  });

  document.getElementById("order-by-button").addEventListener("click", (e) => {
    e.preventDefault();
    disableUI(true);
    showLoading(true);
    if (sortType === 1) {
      sortType = 2;
      sortAsc.classList.add("hidden");
      sortDesc.classList.remove("hidden");
    } else {
      sortType = 1;
      sortAsc.classList.remove("hidden");
      sortDesc.classList.add("hidden");
    }
    handleSearch();
  });

  // Luu data

  function restoreCheckboxesFromURL(paramName, inputName) {
    const searchParams = new URLSearchParams(window.location.search);
    console.log("searchParams: ", searchParams.getAll(paramName));

    searchParams.getAll(paramName).forEach((value) => {
      const checkbox = document.querySelector(
        `input[name="${inputName}"][value="${value}"]`
      );
      console.log(checkbox);
      if (checkbox) checkbox.checked = true;
    });
  }
  restoreCheckboxesFromURL("job_type", "job-type");
  restoreCheckboxesFromURL("job_location", "job-locations");
  restoreCheckboxesFromURL("job_designation", "job_designation");

  rangeFrom.value = searchParams.getAll("range_salary")[0];
  rangeTo.value = searchParams.getAll("range_salary")[1];
  job_name.value = searchParams.get("text_search");

  function handleSearch() {
    console.log("chay handle search");
    const getDataFilterFormCheckBox = (input, array, isNumber) => {
      array.length = 0;
      document.querySelectorAll(input + ":checked").forEach((checkbox) => {
        if (isNumber) {
          array.push(parseFloat(checkbox.value));
        } else {
          array.push(checkbox.value);
        }
        return array;
      });
    };

    getDataFilterFormCheckBox('input[name="job-type"]', jobTypes);
    getDataFilterFormCheckBox('input[name="job-locations"]', jobLocations);
    getDataFilterFormCheckBox('input[name="job_designation"]', jobDesignation);
    rangeSalary =
      rangeFrom.value !== "" || rangeTo.value !== ""
        ? [
            parseInt(document.getElementById("range_from").value) || null,
            parseInt(document.getElementById("range_to").value) || null,
          ]
        : [null, null];
    const data = {
      text_search: job_name.value || "",
      job_type: jobTypes || [],
      job_location: jobLocations || [],
      job_designation: jobDesignation || [],
      range_salary: rangeSalary,
      sort_by: sortType === 1 ? "asc" : "desc",
    };

    updateUrl(data);

    // Hàm để chuyển đổi đối tượng thành chuỗi query parameters
    const buildQueryParams = (data) => {
      const params = [];
      Object.keys(data).forEach((key) => {
        const value = data[key];
        if (value !== null && value !== undefined) {
          // Chuyển đổi giá trị thành chuỗi JSON nếu là đối tượng hoặc mảng
          const stringValue =
            typeof value === "object" ? JSON.stringify(value) : value;
          params.push(`${key}=${stringValue}`);
        }
      });
      return params.join("&");
    };

    // Tạo chuỗi query parameters
    const queryParams = buildQueryParams(data);
    // Gửi dữ liệu đến server
    fetch(
      `/api/method/go1_cms.api.website.jobs.get_all_job?name_section=${name_section}&${queryParams}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((result) => {
        console.log("Success:", result);
        if (result.message.data.length) {
          jobs = result.message.data;
          console.log(jobs);
        } else {
          jobs = [];
        }
        itemsPerPage = result.message.pagination.limit;
        currentPage = result.message.pagination.current_page;
        totalPages = result.message.pagination.total_page;
        displayJobs(currentPage);
      })
      .catch((error) => {
        console.error("Error:", error);
        disableUI(false);
        showLoading(false);
      })
      .finally(() => {
        // Kích hoạt lại các thành phần UI
        disableUI(false);
        showLoading(false);
      });
  }

  function disableUI(disable) {
    // Vô hiệu hóa hoặc kích hoạt các checkbox và nút bấm
    document.getElementById("list-jobs-filter").disabled = disable;
  }

  async function fetchData() {
    try {
      const response = await fetch(
        "/api/method/go1_cms.api.website.jobs.get_filter_job",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      displayJobDesignation(data.message.job_designation);
      console.log(data);
    } catch (error) {
      console.error(
        "There has been a problem with your fetch operation:",
        error
      );
    } finally {
      handleSearch();
    }
  }

  const displayJobDesignation = (jobDesignations) => {
    for (const jobDesignation of jobDesignations) {
      const job_designation_element = document.createElement("div");
      job_designation_element.className = "my-3";
      job_designation_element.innerHTML = `
        <input
            id="${jobDesignation.label}"
            type="checkbox"
            name="job_designation"
            value="${jobDesignation.value}"
            class="mr-2"
        />
        <label for="${jobDesignation.label}">${jobDesignation.label}</label>
      `;
      jobDesignationContainer.appendChild(job_designation_element);
    }

    restoreCheckboxesFromURL("job_designation", "job_designation");
  };

  function showLoading(show) {
    // Hiển thị hoặc ẩn loading indicator
    loadingIndicator.style.display = show ? "flex" : "none";
  }

  function displayJobs(page) {
    const jobContainer = document.getElementById("job-container");
    jobContainer.innerHTML = ""; // Xóa nội dung cũ

    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const paginatedJobs = jobs.slice(startIndex, endIndex);

    if (paginatedJobs?.length === 0) {
      const emptyElement = document.createElement("div");
      emptyElement.className = "flex flex-col justify-center items-center gap-[38px] py-[80px] col-span-full mt-[38px]";
      emptyElement.innerHTML = `
      <img src="/files/empty-state.svg" alt="" class="w-[245.64px] h-[236.85px]"></img>
      <span class="text-[#212B36] font-bold text-xl">Không có kết quả tìm kiếm </span>
      `;
      jobContainer.appendChild(emptyElement);
    } else {
      for (const job of paginatedJobs) {
        const jobElement = document.createElement("div");
        jobElement.className =
          "item h-fit text-left w-full p-6 bg-white border border-gray-200 rounded-lg shadow flex flex-row cursor-pointer";
        jobElement.innerHTML = `
              <div class="w-full">
                  <a href="#">
                      <h5
                          class="text-lg xl:text-xl font-bold tracking-tight text-black"
                      >
                          ${job.job_title}
                      </h5>
                  </a>
                  <div class="flex gap-2 items-center">
                      <div
                          class="text-base font-normal text-[#637381]"
                      >
                          <span>${job.pretty_posted_on}</span>
                      </div>
                  </div>
                  <div class="flex flex-col gap-2 mt-3">
                      <div
                        class="text-gray-500 flex items-center gap-1 lg:pr-[130px]"
                      >
                        <img src="/files/location.svg" alt="" />
                        <span class="font-normal text-base text-[#212B36]"
                          >${job.location}</span
                        >
                      </div>
                      <div
                        class="text-gray-500 flex items-center gap-1 lg:pr-[130px]"
                      >
                        <img src="/files/time.svg" alt="" />
                        <span class="font-normal text-base text-[#212B36]"
                          >${job.employment_type}</span
                        >
                      </div>
                      <div
                        class="text-gray-500 flex items-center gap-1 lg:pr-[130px]"
                      >
                        <img src="/files/range-salary.svg" alt="" />
                        <span class="font-normal text-base text-[#212B36]"
                          >${job.lower_range?.toLocaleString()} - ${job.upper_range?.toLocaleString()} ${
          job.currency
        }</span
                        >
                      </div>
                  </div>
              </div>
              <div class="flex items-start" style="${
                job.designation === null ? "display: none" : ""
              }">
                  <p
                      class="text-sm leading-[18px] py-1 px-2 font-normal text-center text-black border border-[#919EAB52] rounded-lg text-nowrap"
                  >
                      ${job.designation} 
                  </p>
              </div>
            `;
        jobElement.addEventListener("click", () => {
          // Chuyển hướng đến trang chi tiết công việc với ID của công việc
          window.location.href = `/${job.route}`;
        });
        jobContainer.appendChild(jobElement);
      }
    }

    // Hiển thị các nút phân trang
    displayPagination();
    window.scrollTo(0, 400);
  }

  function displayPagination() {
    const paginationContainer = document.getElementById("pagination-container");
    const prevPage = document.getElementById("prev-page");
    const nextPage = document.getElementById("next-page");

    currentPage === 1
      ? prevPage?.classList.add("no-active")
      : prevPage.classList.remove("no-active");
    currentPage === totalPages
      ? nextPage?.classList.add("no-active")
      : nextPage.classList.remove("no-active");

    totalPages > 1
      ? paginationContainer.classList.remove("hidden")
      : paginationContainer.classList.add("hidden");

    paginationContainer
      ?.querySelectorAll(".page-number")
      ?.forEach((button) => button.remove());

    for (let i = 1; i <= totalPages; i++) {
      const pageButton = document.createElement("button");
      pageButton.innerText = i;
      pageButton.className = `page-number text-sm text-[#222222] font-normal px-2 py-[1px] ${
        i === currentPage ? "active" : ""
      }`;
      pageButton.addEventListener("click", () => {
        currentPage = i;
        displayJobs(currentPage);
      });
      paginationContainer?.insertBefore(
        pageButton,
        document.getElementById("next-page")
      );
    }
  }

  // Thêm sự kiện click cho nút Previous
  document.getElementById("prev-page").addEventListener("click", () => {
    if (currentPage > 1) {
      currentPage--;
      displayJobs(currentPage);
    }
  });

  // Thêm sự kiện click cho nút Next
  document.getElementById("next-page").addEventListener("click", () => {
    if (currentPage < totalPages) {
      currentPage++;
      displayJobs(currentPage);
    }
  });

  // Gọi hàm hiển thị lần đầu tiên
  fetchData();
  displayJobs(currentPage);
});

