  .getElementById("contact-submit-button")
  .addEventListener("click", (e) => {
    e.preventDefault();

    if (full_name === "") {
      document.getElementById("full_name_error").innerHTML =
        "Họ tên không được để trống";
    } else {
      document.getElementById("full_name_error").innerHTML = "";
    }

