document.addEventListener("DOMContentLoaded", function () {
        const links = document.querySelectorAll("#map-links #map-link");
        const iframeContainer = document.getElementById("map-iframe");

        const setWidthIfame = () => {
          setTimeout(() => {
            const iframe = iframeContainer.querySelector("iframe");
            if (iframe) {
              iframe.classList.add("w-full"); // Thêm class w-full cho iframe
            }
          }, 0);
        };

        // Đặt nội dung iframe mặc định là liên kết đầu tiên
        if (links.length > 0) {
          iframeContainer.innerHTML = links[0].getAttribute("data-url");
          setWidthIfame();
        }

        // Đánh dấu liên kết đầu tiên được chọn
        links[0].classList.add("map-selected");

        // Thêm sự kiện click cho tất cả các liên kết
        links.forEach((link) => {
          link.addEventListener("click", function (event) {
            event.preventDefault(); // Ngăn chặn hành vi mặc định thay đổi URL
            links.forEach((l) => l.classList.remove("map-selected")); // Bỏ class đã chọn trước đó

            const iframeHTML = this.getAttribute("data-url");
            iframeContainer.innerHTML = iframeHTML; // Thay thế toàn bộ khối iframe bằng nội dung HTML từ data-url
            setWidthIfame();
            link.classList.add("map-selected"); // Đánh dấu liên kết được chọn
          });
        });
      });

