// code update url
const updateUrl = (data) => {
  const params = new URLSearchParams();
  // Duyệt qua các thuộc tính của object `data` và thêm chúng vào params
  Object?.keys(data).forEach((key) => {
    if (Array.isArray(data[key])) {
      // Nếu là mảng, thì thêm từng giá trị của mảng vào params với cùng một key
      data[key].forEach((value) => params.append(key, value));
    } else {
      // Nếu không phải mảng, thêm trực tiếp vào params
      params.set(key, data[key]);
    }
  });

  const newUrl = `${
    window.location.pathname
  }?page_no=${currentPage}&${params.toString()}`;
  history.replaceState(null, "", newUrl);
};

// code tao list filter

const getFilter = async () => {
  fetch(apiGetFilter, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    }
  })
    .then((response) => response.json())
    .then((result) => {
      console.log("Success:", result);
      displayFilterProduct(result.message);
      for (const filter of result.message.attribute_filters) {
        itemAttributeFilter.push(filter.key);
      }
      updateUrl();
    })
    .catch((error) => {
      console.error("Error:", error);
    })
    .finally(() => {
      // Kích hoạt lại các thành phần UI bị vô hiệu hóa
      restoreCheckboxesFromURL("item_group", "item_group");
      restoreCheckboxesFromURL("brand", "brand");
      for (const filter of itemAttributeFilter) {
        restoreCheckboxesFromURL(`${filter}`, `${filter}`);
      }
      getProducts();
    });
};

