// Luu data

function restoreCheckboxesFromURL(paramName, inputName) {
  const searchParams = new URLSearchParams(window.location.search);
  console.log("searchParams: ", searchParams.getAll(paramName));

  searchParams.getAll(paramName).forEach((value) => {
    const checkbox = document.querySelector(
      `input[name="${inputName}"][value="${value}"]`
    );
    console.log(checkbox);
    if (checkbox) checkbox.checked = true;
  });
}

// code lay du lieu san pham

const getProducts = () => {
  disableUI(true);
  showLoading(true);
  const getDataFilterFormCheckBox = (input, array, isNumber) => {
    array.length = 0;
    document.querySelectorAll(input + ":checked").forEach((checkbox) => {
      if (isNumber) {
        array.push(parseFloat(checkbox.value));
      } else {
        array.push(checkbox.value);
      }
      return array;
    });
  };
  getDataFilterFormCheckBox('input[name="item_group"]', itemGroup);
  getDataFilterFormCheckBox('input[name="brand"]', itemBrand);

  const data = {
    textSearch: textSearch.value,
    item_group: itemGroup,
    brand: itemBrand
  };

  for (const filter of itemAttributeFilter) {
    const nameFilter = [];
    getDataFilterFormCheckBox(`input[name="${filter}"]`, nameFilter);
    data[filter] = nameFilter;
  }
  updateUrl(data);

  const attributeFilters = {};
  const fieldFilters = {};

  // Assuming `itemAttributeFilter` contains keys like 'Colour'
  for (const key in data) {
    if (key !== "textSearch") {
      if (itemAttributeFilter.includes(key)) {
        attributeFilters[key] = data[key];
      } else {
        fieldFilters[key] = data[key];
      }
    }
  }
  console.log(attributeFilters);
  // Kiểm tra xem đối tượng attributeFilters có chứa mảng không rỗng, nếu có thì thêm vào mảng filterdAttributeFilters
  const filteredAttributeFilters = Object.keys(attributeFilters).reduce(
    (result, key) => {
      if (attributeFilters[key].length > 0) {
        result[key] = attributeFilters[key];
      }
      return result;
    },
    {}
  );

  // Kiểm tra xem đối tượng fieldFilters có chứa mảng không rỗng, nếu có thì thêm vào mảng filterdFieldFilters
  const filteredFieldFilters = Object.keys(fieldFilters).reduce(
    (result, key) => {
      if (fieldFilters[key].length > 0) {
        result[key] = fieldFilters[key];
      }
      return result;
    },
    {}
  );
  let queryString = "&";
  if (Object.keys(filteredAttributeFilters).length > 0) {
    queryString += `attribute_filters=${JSON.stringify(
      filteredAttributeFilters
    )}`;
  }
  if (Object.keys(filteredFieldFilters).length > 0) {
    if (queryString) queryString += "&";
    queryString += `field_filters=${JSON.stringify(filteredFieldFilters)}`;
  }

  fetch(`${apiGetProduct}?page_no=${currentPage}${queryString}`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json"
    }
  })
    .then((response) => response.json())
    .then((result) => {
      console.log("Success:", result);
      if (result.message.data) {
        totalPages = result.message.pagination.total_page;
        currentPage = result.message.pagination.current_page;
        itemsPerPage = result.message.pagination.limit;
        displayProduct(result.message.data);
      }
    })
    .catch((error) => {
      console.error("Error:", error);
       disableUI(false);
       showLoading(false);
    })
    .finally(() => {
      // Kích hoạt lại các thành phần UI bị vô hiệu hóa
        disableUI(false);
        showLoading(false);
    });
};

// hieen thi san pham
const displayProduct = (products) => {
  productContainer.innerHTML = "";
  if (products.length === 0) {
    const emptyElement = document.createElement("div");
    emptyElement.className =
      "flex flex-col justify-center items-center gap-[38px] py-[80px] col-span-full mt-[38px]";
    emptyElement.innerHTML = `
    <img src="/files/empty-state.svg" alt="" class="w-[245.64px] h-[236.85px]"></img>
    <span class="text-[#212B36] font-bold text-xl">Không có kết quả tìm kiếm </span>
    `;
    productContainer.appendChild(emptyElement);
  } else {
    for (const product of products) {
      const productElement = document.createElement("a");

      productElement.className =
        "flex flex-col gap-[30px] cursor-pointer shadow-md";
      productElement.href = `/${product.route}`;
      productElement.innerHTML = `
            <div>
                <img
                src="${product.website_image}"
                alt=""
                class="h-[242px] w-full"
                />
                </div>
                    <div class="p-2.5 flex flex-col gap-7">
                        <span
                            class="font-medium text-base text-[#3C3B3B] leading-[19.6px]"
                        >${product.web_item_name}</span
                    >
                    <span class="text-[#FF5E14] font-normal text-xl"
                        >${
                          product.price_list_rate?.toLocaleString() || "Liên hệ"
                        }</span
                    >
                </div>
            `; // Add closing brace here
      productContainer.appendChild(productElement);
    }
  }

  displayPagination();
  window.scrollTo(0, 0);
};

// code xu ly su kien

formFilter.addEventListener("change", (event) => {
  currentPage = 1;
  getProducts();
});

// code xu ly phan trang

function displayPagination() {
  const paginationContainer = document.getElementById("pagination-container");
  const prevPage = document.getElementById("prev-page");
  const nextPage = document.getElementById("next-page");

  currentPage === 1
    ? prevPage?.classList.add("no-active")
    : prevPage.classList.remove("no-active");
  currentPage === totalPages
    ? nextPage?.classList.add("no-active")
    : nextPage.classList.remove("no-active");

  totalPages > 1
    ? paginationContainer.classList.remove("hidden")
    : paginationContainer.classList.add("hidden");

  paginationContainer
    ?.querySelectorAll(".page-number")
    ?.forEach((button) => button.remove());

  for (let i = 1; i <= totalPages; i++) {
    const pageButton = document.createElement("button");
    pageButton.innerText = i;
    pageButton.className = `page-number text-lg text-[#222222] font-normal px-2 py-[1px] ${
      i === currentPage ? "active" : ""
    }`;
    pageButton.addEventListener("click", () => {
      if (currentPage !== i) {
        currentPage = i;
        getProducts();
      }
    });
    paginationContainer?.insertBefore(
      pageButton,
      document.getElementById("next-page")
    );
  }
}

// Thêm sự kiện click cho nút Previous
document.getElementById("prev-page").addEventListener("click", () => {
  if (currentPage > 1) {
    currentPage--;
    getProducts();
  }
});

// Thêm sự kiện click cho nút Next
document.getElementById("next-page").addEventListener("click", () => {
  if (currentPage < totalPages) {
    currentPage++;
    getProducts();
  }
});

//loading
function showLoading(show) {
  // Hiển thị hoặc ẩn loading indicator
  loadingIndicator.style.display = show ? "flex" : "none";
}

function disableUI(disable) {
  // Vô hiệu hóa hoặc kích hoạt các checkbox và nút bấm
  document.getElementById("list-products-filter").disabled = disable;
}

textSearch.addEventListener("input", () => {
  clearTimeout(changeTimer);
  changeTimer = setTimeout(() => {
    currentPage = 1;
    getProducts();
  }, changeInterval);
});

