let jobs = [];
let itemsPerPage = 9;
let currentPage = 1;
let totalPages = 1;

const searchParams = new URLSearchParams(window.location.search);

const filterForm = document.getElementById("formFilter");
const loadingIndicator = document.getElementById("loading");
const resetFilter = document.getElementById("resetFilter");
const job_name = document.getElementById("job-name");
const listCategoryFilter = document.getElementById("list-category-filter");

let changeTimer; // Timer identifier
let changeInterval = 1000; // Thời gian chờ (ms)

const radioButtons = document.querySelectorAll('input[name="category"]');
let selectedCategory = "";

const name_section = "{{ name_section }}";

const listCategory = [];

document.getElementById("job-name").addEventListener("input", function () {
  clearTimeout(changeTimer);
  changeTimer = setTimeout(doneChanging, changeInterval);
});

// Hàm xử lý sau khi giá trị input thay đổi
function doneChanging() {
  disableUI(true);
  showLoading(true);
  handleSearch();
}

resetFilter.addEventListener("click", (e) => {
  e.preventDefault();
  job_name.value = "";
  selectedCategory = "";
  currentPage = 1;

  const data = {
    text_search: job_name.value || "",
    blog_category: selectedCategory || "",
    page_no: currentPage
  };
  const radioButton = document.querySelector(
    `input[name="category"][value="${selectedCategory}"]`
  );
  console.log(radioButton);
  if (radioButton) {
    radioButton.checked = true;
  }

  updateUrl(data);
  setTimeout(() => {
    handleSearch();
  }, 0);
});

const updateUrl = (data) => {
  const params = new URLSearchParams();
  // Duyệt qua các thuộc tính của object `data` và thêm chúng vào params
  Object.keys(data).forEach((key) => {
    if (Array.isArray(data[key])) {
      // Nếu là mảng, thì thêm từng giá trị của mảng vào params với cùng một key
      data[key].forEach((value) => params.append(key, value));
    } else {
      // Nếu không phải mảng, thêm trực tiếp vào params
      params.set(key, data[key]);
    }
  });

  const newUrl = `${window.location.pathname}?${params.toString()}`;
  history.replaceState(null, "", newUrl);
};

filterForm.addEventListener("change", (e) => {
  e.preventDefault();
  const radioButtons = document.querySelectorAll('input[name="category"]');

  radioButtons.forEach((radio) => {
    if (radio.checked) {
      selectedCategory = radio.value;
    }
  });
  doneChanging();
});

function disableUI(disable) {
  // Vô hiệu hóa hoặc kích hoạt các checkbox và nút bấm
  document.getElementById("list-tips-beautiful").disabled = disable;
}

// Luu data

function restoreRadioFromURL(paramName, inputName) {
  const searchParams = new URLSearchParams(window.location.search);
  const selectedValue = searchParams.get(paramName);

  if (selectedValue || selectedValue === "") {
    const radioButton = document.querySelector(
      `input[name="${inputName}"][value="${selectedValue}"]`
    );
    console.log(radioButton);
    if (radioButton) {
      radioButton.checked = true;
    }
  }
  return selectedValue;
}

job_name.value = searchParams.get("text_search");

function handleSearch() {
  const data = {
    text_search: job_name.value || "",
    blog_category: selectedCategory || "",
    page_no: currentPage
  };

  updateUrl(data);
  // Gửi dữ liệu đến server
  fetch(
    `/api/method/go1_cms.api.website.blog.get_blog_list?name_section=${name_section}&text_search=${job_name.value}&blog_category=${selectedCategory || ''}&page_no=${currentPage}`,
    {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    }
  )
    .then((response) => response.json())
    .then((result) => {
      console.log("Success:", result);
      if (result.message.data.length) {
        jobs = result.message.data;
        console.log(jobs);
      } else {
        jobs = [];
      }
      itemsPerPage = result.message.pagination.limit;
      currentPage = result.message.pagination.current_page;
      totalPages = result.message.pagination.total_page;
      displayJobs(currentPage);
    })
    .catch((error) => {
      console.error("Error:", error);
      disableUI(false);
      showLoading(false);
    })
    .finally(() => {
      // Kích hoạt lại các thành phần UI
      disableUI(false);
      showLoading(false);
    });
}

function displayJobs(page) {
  const jobContainer = document.getElementById("newsContainer");
  jobContainer.innerHTML = ""; // Xóa nội dung cũ

  if (jobs?.length === 0) {
    const emptyElement = document.createElement("div");
    emptyElement.className =
      "flex flex-col justify-center items-center gap-[38px] py-[80px] col-span-full mt-[38px]";
    emptyElement.innerHTML = `
      <img src="/files/empty-state.svg" alt="" class="w-[245.64px] h-[236.85px]"></img>
      <span class="text-[#212B36] font-bold text-xl">Không có kết quả tìm kiếm </span>
      `;
    jobContainer.appendChild(emptyElement);
  } else {
    for (const job of jobs) {
      const jobElement = document.createElement("div");
      jobElement.className = "flex flex-col gap-[10px]";
      jobElement.innerHTML = `                  
        <div>
            <img
                src="${job.meta_image}"
                alt=""
                class="h-[196px] w-full"
            />
        </div>
        <div class="flex justify-between">
            <div>
                <i class="fa fa-clock-o" aria-hidden="true"></i>
                <span class="font-normal text-sm span-black"
                    >${job.published_on}</span
                >
                </div>
            <div class="">
                <i class="fa fa-user" aria-hidden="true"></i>
                <span class="text-sm font-normal span-black"
                >${job.blogger}</span
                >
            </div>
        </div>
            <span class="font-medium text-[22px] span-black text-hide"
                >${job.title}</span
            >
            <span
                class="text-container font-normal text-sm span-black min-h-[60px]"
            >
                ${job.blog_intro}</span
            >
            <a href="/${job.route}"
                class="flex border text-[#919EAB52] w-fit p-2 rounded-lg"
            >
                <span class="font-medium text-sm text-[#78715A] mr-1"
                    >Tìm hiểu thêm</span
                >
            <svg
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
            >
                <path
                d="M19.9219 11.625C19.9531 11.6875 19.9727 11.75 19.9805 11.8125C19.9883 11.875 19.9922 11.9453 19.9922 12.0234C19.9922 12.1484 19.9688 12.2734 19.9219 12.3984C19.875 12.5234 19.8047 12.6328 19.7109 12.7266L12.7031 19.7344C12.6094 19.8125 12.5039 19.875 12.3867 19.9219C12.2695 19.9688 12.1484 19.9922 12.0234 19.9922C11.7422 19.9922 11.5039 19.8945 11.3086 19.6992C11.1133 19.5039 11.0156 19.2734 11.0156 19.0078C11.0156 18.8672 11.0391 18.7383 11.0859 18.6211C11.1328 18.5039 11.2031 18.3984 11.2969 18.3047L16.5938 13.0078H4.99219C4.72656 13.0078 4.49609 12.9102 4.30078 12.7148C4.10547 12.5195 4.00781 12.2891 4.00781 12.0234C4.00781 11.7422 4.10547 11.5039 4.30078 11.3086C4.49609 11.1133 4.72656 11.0156 4.99219 11.0156H16.5938L11.2969 5.71875C11.2031 5.625 11.1289 5.51953 11.0742 5.40234C11.0195 5.28516 10.9922 5.14844 10.9922 4.99219C10.9922 4.72656 11.0898 4.49609 11.2852 4.30078C11.4805 4.10547 11.7109 4.00781 11.9766 4.00781C12.1328 4.00781 12.2695 4.03516 12.3867 4.08984C12.5039 4.14453 12.6094 4.21875 12.7031 4.3125L19.7109 11.3203C19.7578 11.3516 19.7969 11.3945 19.8281 11.4492C19.8594 11.5039 19.8906 11.5625 19.9219 11.625Z"
                fill="#78715A"
            />
            </svg>
            </a>

            `;
      jobContainer.appendChild(jobElement);
    }
  }

  // Hiển thị các nút phân trang
  displayPagination();
  window.scrollTo(0, 0);
}

function displayPagination() {
  const paginationContainer = document.getElementById("pagination-container");
  const prevPage = document.getElementById("prev-page");
  const nextPage = document.getElementById("next-page");

  currentPage === 1
    ? prevPage?.classList.add("no-active")
    : prevPage.classList.remove("no-active");
  currentPage === totalPages
    ? nextPage?.classList.add("no-active")
    : nextPage.classList.remove("no-active");

  totalPages > 1
    ? paginationContainer.classList.remove("hidden")
    : paginationContainer.classList.add("hidden");

  paginationContainer
    ?.querySelectorAll(".page-number")
    ?.forEach((button) => button.remove());

  for (let i = 1; i <= totalPages; i++) {
    const pageButton = document.createElement("button");
    pageButton.innerText = i;
    pageButton.className = `page-number text-sm text-[#222222] font-normal px-2 py-[1px] ${
      i === currentPage ? "active" : ""
    }`;
    pageButton.addEventListener("click", () => {
      currentPage = i;
      displayJobs(currentPage);
    });
    paginationContainer?.insertBefore(
      pageButton,
      document.getElementById("next-page")
    );
  }
}

// Hàm xử lý khi người dùng thay đổi bộ lọc
async function fetchData() {
  try {
    const response = await fetch(
      "/api/method/go1_cms.api.website.blog.get_categories",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      }
    );
    if (!response.ok) {
      throw new Error("Network response was not ok");
    }
    const data = await response.json();
    displayCategory(data.message.data);
    console.log(data);
  } catch (error) {
    console.error("There has been a problem with your fetch operation:", error);
  } finally {
    selectedCategory = restoreRadioFromURL("blog_category", "category");
    handleSearch();
  }
}

const displayCategory = (listCategory) => {
  for (const category of listCategory) {
    const category_element = document.createElement("div");
    category_element.className = "flex items-center mb-2";
    category_element.innerHTML = `
    <input
        type="radio"
        id="${category.label}"
        name="category"
        value="${category.value}"
        class="hidden"
    />
    <label
        for="${category.label}"
            class="cursor-pointer hover:font-semibold"
        >${category.label}</label
    >           
      `;
    listCategoryFilter.appendChild(category_element);
  }
};

// Kết thúc hàm xử lý khi người dùng thay đổi bộ lọc

function showLoading(show) {
  // Hiển thị hoặc ẩn loading indicator
  loadingIndicator.style.display = show ? "flex" : "none";
}

// Thêm sự kiện click cho nút Previous
document.getElementById("prev-page").addEventListener("click", () => {
  if (currentPage > 1) {
    currentPage--;
    displayJobs(currentPage);
  }
});

// Thêm sự kiện click cho nút Next
document.getElementById("next-page").addEventListener("click", () => {
  if (currentPage < totalPages) {
    currentPage++;
    displayJobs(currentPage);
  }
});

fetchData();

