console.log('sdmmm')
        const speed = 500; // Tốc độ đếm

        counters.forEach((counter) => {
          const updateCount = () => {
            const target = +counter.getAttribute("data-target");
            const count = +counter.innerText.replace(/,/g, ""); // Loại bỏ dấu phẩy nếu có

            // Tính toán số tăng thêm mỗi lần
            const increment = target / speed;

            // Nếu số hiện tại vẫn nhỏ hơn số đích thì tiếp tục tăng
            if (count < target) {
              counter.innerText = Math.ceil(count + increment).toLocaleString();
              setTimeout(updateCount, 1);
            } else {
              counter.innerText = target.toLocaleString(); // Đảm bảo số hiển thị là số đích với dấu phần nghìn
            }
          };

          updateCount();
        });

