function caculator(number) {
  const promise = new Promise(function (resolve, reject) {
      setTimeout(function() {
          if(typeof number !== 'number') {
              return reject(new Error("sai kieu r"))
          }
          const result = number * number;
          resolve(result);
      }, 1000)
  });
  return promise
}

