     function updateTotal() {
       let total = 140000;
       const remainingItems = document.querySelectorAll("tbody tr");
       remainingItems.forEach((item) => {
         const subtotal = parseFloat(
           item
             .querySelector(".subtotal")
             .textContent.replace(/đ/g, "")
             .replace(/\./g, "")
             .replace(/,/g, "")
         );
         total += subtotal;
       });
       totalAmount.textContent = total.toLocaleString("vi-VN") + "đ";

       const discount =
         parseFloat(
           discountElement.textContent
             .replace(/\./g, "")
             .replace(/,/g, "")
             .replace(/đ/g, "")
         ) || 0;
       const shippingFee =
         parseFloat(
           shippingFeeElement.textContent
             .replace(/\./g, "")
             .replace(/,/g, "")
             .replace(/đ/g, "")
         ) || 0;
       const finalTotal = total - discount + shippingFee;
       finalAmount.textContent = finalTotal.toLocaleString("vi-VN") + "đ";
     }

     applyVoucherButton.addEventListener("click", () => {
       const voucherValue =
         parseFloat(voucherInput.value.replace(/\./g, "").replace(/,/g, "")) ||
         0;
       discountElement.textContent =
         voucherValue.toLocaleString("vi-VN") + "đ";
       updateTotal();
     });

     updateTotal();

