document.addEventListener("DOMContentLoaded", function () {
       const cartItems = document.querySelectorAll("tbody tr");
       const totalAmount = document.querySelector(".total-amount");
       const emptyCartMessage = document.querySelector('.empty-cart');
       const finalAmount = document.querySelector(".final-amount");
       const discountElement = document.querySelector(".discount");
       const shippingFeeElement = document.querySelector(".shipping-fee");
       const voucherInput = document.querySelector(".voucher-input");
       const applyVoucherButton = document.querySelector(".apply-voucher");

       function updateTotal() {
         let total = 0;
         const remainingItems = document.querySelectorAll("tbody tr");
         remainingItems.forEach((item) => {
           const subtotal = parseFloat(
             item
               .querySelector(".subtotal")
               .textContent.replace(/đ/g, "")
               .replace(/\./g, "")
               .replace(/,/g, "")
           );
           total += subtotal;
         });
         totalAmount.textContent = total.toLocaleString("vi-VN") + "đ";

         const discount =
           parseFloat(
             discountElement.textContent
               .replace(/\./g, "")
               .replace(/,/g, "")
               .replace(/đ/g, "")
           ) || 0;
         const shippingFee =
           parseFloat(
             shippingFeeElement.textContent
               .replace(/\./g, "")
               .replace(/,/g, "")
               .replace(/đ/g, "")
           ) || 0;
         const finalTotal = total - discount + shippingFee;
         finalAmount.textContent = finalTotal.toLocaleString("vi-VN") + "đ";
         checkEmptyCart();
       }

       function checkEmptyCart() {
         const remainingItems = document.querySelectorAll("tbody tr");
         console.log(remainingItems);
         if (remainingItems.length === 0) {
           document.querySelector(".cart-items").classList.add("hidden");
           emptyCartMessage.classList.remove("hidden");
         } else {
           document.querySelector(".cart-items").classList.remove("hidden");
           emptyCartMessage.classList.add("hidden");
         }
       }

       cartItems.forEach((item) => {
         const btnDecrease = item.querySelector(".btn-decrease");
         const btnIncrease = item.querySelector(".btn-increase");
         const btnDelete = item.querySelector(".btn-delete");
         const quantityElement = item.querySelector(".quantity");
         const subtotalElement = item.querySelector(".subtotal");
         const price = parseFloat(
           item.children[1].textContent
             .replace(/đ\/kg/g, "")
             .replace(/\./g, "")
             .replace(/,/g, "")
         );

         btnDecrease.addEventListener("click", () => {
           let quantity = parseInt(quantityElement.textContent);
           if (quantity > 1) {
             quantity--;
             quantityElement.textContent = quantity;
             subtotalElement.textContent =
               (quantity * price).toLocaleString("vi-VN") + "đ";
             updateTotal();
           }
         });

         btnIncrease.addEventListener("click", () => {
           let quantity = parseInt(quantityElement.textContent);
           quantity++;
           quantityElement.textContent = quantity;
           subtotalElement.textContent =
             (quantity * price).toLocaleString("vi-VN") + "đ";
           updateTotal();
         });

         btnDelete.addEventListener("click", () => {
           item.remove();
           updateTotal();
         });
       });

       applyVoucherButton.addEventListener("click", () => {
         const voucherValue =
           parseFloat(
             voucherInput.value.replace(/\./g, "").replace(/,/g, "")
           ) || 0;
         discountElement.textContent =
           voucherValue.toLocaleString("vi-VN") + "đ";
         updateTotal();
       });

       // Initialize total calculation
       updateTotal();
     });

