let limit = 6;
let list = document.querySelectorAll(".list-info .item");

function updateLimit() {
  if (window.innerWidth < 1024) {
    limit = 6;
  } else {
    limit = 6;
  }
}

function loadItem() {
  updateLimit();
  let beginGet = limit * (thisPage - 1);
  let endGet = limit * thisPage - 1;
  list.forEach((item, key) => {
    if (key >= beginGet && key <= endGet) {
      item.style.display = "block";
    } else {
      item.style.display = "none";
    }
  });
  listPage();
}

function listPage() {
  let count = Math.ceil(list.length / limit);
  let listPageElement = document.querySelector(".listPage");
  listPageElement.innerHTML = "";

  if (list.length < limit) {
    listPageElement.style.display = "none";
    return;
  } else {
    listPageElement.style.display = "block";
  }

  let prev = document.createElement("li");
  prev.innerText = "<";
  if (thisPage === 1) {
    prev.classList.add("blocks");
  } else {
    prev.setAttribute("onclick", "changePage(" + (thisPage - 1) + ")");
  }
  listPageElement.appendChild(prev);

  for (let i = 1; i <= count; i++) {
    let newPage = document.createElement("li");
    newPage.innerText = i;
    if (i == thisPage) {
      newPage.classList.add("active");
    }
    newPage.setAttribute("onclick", "changePage(" + i + ")");
    listPageElement.appendChild(newPage);
  }

  let next = document.createElement("li");
  next.innerText = ">";
  if (thisPage === count) {
    next.classList.add("blocks");
  } else {
    next.setAttribute("onclick", "changePage(" + (thisPage + 1) + ")");
  }
  listPageElement.appendChild(next);
}

function changePage(i) {
  let count = Math.ceil(list.length / limit);
  if (i >= 1 && i <= count) {
    thisPage = i;
    loadItem();
  }
}

window.addEventListener("resize", () => {
  thisPage = 1; // Reset to the first page on resize
  loadItem();
});

loadItem();

