var speed = 15;
          var tab = document.getElementById("demo_{{class_name}}");
          var tab1 = document.getElementById("demo1_{{class_name}}");
          var tab2 = document.getElementById("demo2_{{class_name}}");
          tab2.innerHTML = tab1.innerHTML;
          function Marquee() {
            if (tab2.offsetWidth - tab.scrollLeft <= 0)
              tab.scrollLeft = 0;
            else {
              tab.scrollLeft++;
            }
          }
          var MyMar = setInterval(Marquee, speed);
          tab.onmouseover = function () {
            clearInterval(MyMar);
          };
          tab.onmouseout = function () {
            MyMar = setInterval(Marquee, speed);
          };

