// script.js

// Hien thi star

var starContainers = document.querySelectorAll(".star-container");

starContainers.forEach(function (container) {
  var rating = parseInt(container.getAttribute("data-rating"));
  var stars = container.querySelectorAll(".fa-star");

  // Set the checked class to the stars according to the rating
  for (var i = 0; i < rating; i++) {
    stars[i].classList.add("checked");
  }
});

// tinh star trung binh

var ratings = [10, 5, 4, 3, 2]; // Ratings for 5, 4, 3, 2, 1 stars
var totalReviews = ratings.reduce((a, b) => a + b, 0);
var averageRating =
  ratings.reduce((sum, count, index) => sum + count * (5 - index), 0) /
  totalReviews;
console.log(Math.round(averageRating));

// Update the average rating and reviews count
document.querySelector(".rating-value").textContent = averageRating.toFixed(1);
document.querySelector(".reviews").textContent = `${totalReviews} reviews`;

// Update the stars
var stars = document.querySelector(".average-rating .stars").children;
for (var i = 0; i < 5; i++) {
  stars[i].className = "fa fa-star"; // Reset to default
  if (i < Math.floor(averageRating)) {
    stars[i].classList.add("checked");
  } else if (i < Math.ceil(averageRating)) {
    stars[i].className = "fa fa-star-half-o checked";
  }
}

// Update the progress bars
var progressBars = document.querySelectorAll(".progress-bar .progress div");
for (var i = 0; i < progressBars.length; i++) {
  var percentage = (ratings[i] / totalReviews) * 100;
  progressBars[i].style.width = percentage + "%";
}

