    setQueryParameter("blog_view", view);
  }
  function pageChangeActive(currentPage, totalPage) {
    // arrow left
    setQueryParameter("page", currentPage);
    if (currentPage > 1) {
      $(".pagination__arrow__left").addClass("active");
      $(".pagination__arrow__left__start").addClass("active");
    } else {
      $(".pagination__arrow__left").removeClass("active");
      $(".pagination__arrow__left__start").removeClass("active");
    }
    // arrow right
    if (currentPage < totalPage) {
      $(".pagination__arrow__right").addClass("active");
      $(".pagination__arrow__right__end").addClass("active");
    } else {
      $(".pagination__arrow__right").removeClass("active");
      $(".pagination__arrow__right__end").removeClass("active");
    }
    // page
    $(".pagination__page").removeClass("active");
    $(`.pagination__page[data-page="${currentPage}"]`).addClass("active");
  }

