      data: {
        name_section: "{{ name_section }}",
        page_no: page_no,
        text_search: text_search,
      },
      dataType: "json",
      headers: {
        "X-Frappe-CSRF-Token": "{{csrf_token}}",
      },
      success: function (data) {
        let blogs = data.message?.blogs;
        let recent_blogs = data.message?.recent_blogs;

        // load blogs
        if (blogs && blogs.length) {
          totalPage = data.message?.total_page || 0;
          // render blog data
          let html = "";
          $(blogs).each(function (k, v) {
            html += `
