document
        .getElementById("upload-container")
        .addEventListener("click", function () {
          document.getElementById("file_cv").click();
        });

      document
        .getElementById("file_cv")
        .addEventListener("change", function () {
          var file = this.files[0];
          var fileError = document.getElementById("file-error");
          var fileName = document.getElementById("file-name");
          fileError.classList.add("hidden");
          fileName.classList.add("hidden");

          if (
            file.size > 10 * 1024 * 1024 ||
            ![
              "application/pdf",
              "application/msword",
              "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
            ].includes(file.type)
          ) {
            fileError.classList.remove("hidden");
            this.value = "";
          } else {
            fileName.textContent = "File uploaded: " + file.name;
            fileName.classList.remove("hidden");
          }
        });

      document
        .getElementById("application-form")
        .addEventListener("submit", function (event) {
          event.preventDefault();

          var formData = new FormData(this);
          var fileError = document.getElementById("file-error");

          if (!formData.get("file_cv")) {
            fileError.classList.remove("hidden");
            return;
          }

          var application_method = [];
          document
            .querySelectorAll('input[name="application_method"]:checked')
            .forEach(function (checkbox) {
              application_method.push(checkbox.value);
            });
          formData.delete("application_method"); 
          formData.append("application_method", JSON.stringify(application_method)); 

          fetch("YOUR_API_ENDPOINT", {
            method: "POST",
            body: formData,
          })
            .then((response) => response.json())
            .then((data) => {
              if (data.success) {
                alert("Form submitted successfully");
              } else {
                alert("Error submitting form");
              }
            })
            .catch((error) => {
              console.error("Error:", error);
            });
        });

