.filter-toggle {
        display: none;
        position: fixed;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
        background: #212B36;
        color: white;
        border: none;
        padding: 10px;
        border-radius: 5px;
        cursor: pointer;
        width: 64px;
        height: 48px;
      }

      .overlay {
        display: none;
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: rgba(0, 0, 0, 0.5);
        z-index: 999;
      }

      @media (max-width: 1200px) {
        .filter {
          position: fixed;
          top: 0;
          right: 0;
          height: 100%;
          width: 70%;
          z-index: 1000;
          transform: translateX(-100%);
          transition: transform 0.3s ease;
          overflow-y: auto;
          display: flex;
          justify-content: flex-end;
          background-color: rgba(0, 0, 0, -0.5);
        }

        .display {
          display: none;
        }

        .filter.active {
          transform: translateX(0);
        }

        .filter-toggle {
          display: block;
        }

        .overlay.active {
          display: block;
        }
      }

/* Overlay for the entire screen */
      .loading-overlay {
        top: 50%;
        transform: translateY(-50%);
        width: 100%;
        height: 100%;
        background: rgba(255, 255, 255, 0.8);
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 9999; /* Ensure it is above all other elements */
        display: none; /* Hidden by default */
      }

      /* Spinner */
      .spinner {
        border: 4px solid rgba(0, 0, 0, 0.1);
        border-top: 4px solid #3498db;
        border-radius: 50%;
        width: 40px;
        height: 40px;
        animation: spin 1s linear infinite;
      }

      /* Spinner animation */
      @keyframes spin {
        0% {
          transform: rotate(0deg);
        }
        100% {
          transform: rotate(360deg);
        }
      }

 .page-number.active {
