      /* Position the image container (needed to position the left and right arrows) */
      .container {
      }

      /* Hide the images by default */
      .mySlides {
        height: 500px;
        position: relative;
      }

      .mySlides img {
        height: 500px;
      }

      /* Add a pointer when hovering over the thumbnail images */
      .cursor {
        cursor: pointer;
      }

      /* Next & previous buttons */
      .prev,
      .next {
        cursor: pointer;
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        color: white;
        font-weight: 400;
        font-size: 10px;
        border-radius: 100px;
        user-select: none;
        -webkit-user-select: none;
        background-color: #225771;
        width: 36px;
        height: 36px;
        display: flex;
        align-items: center;
        justify-content: center;
        z-index: 9;
      }

      /* Position the "next button" to the right */
      .next {
        right: 0;
      }

      .prev {
        left: 0;
      }

      /* On hover, add a black background color with a little bit see-through */
      .prev:hover,
      .next:hover {
        background-color: rgba(0, 0, 0, 0.8);
      }

      /* Number text (1/3 etc) */
      .numbertext {
        color: #f2f2f2;
        font-size: 12px;
        padding: 8px 12px;
        position: absolute;
        top: 0;
      }

      /* Container for image text */
      .caption-container {
        text-align: center;
        background-color: #222;
        padding: 2px 16px;
        color: white;
      }

      .row {
        display: flex;
      }

      /* Six columns side by side */
      .column {
        display: inline-block;
        margin-right: 5px;
      }

      .column img {
        border-radius: 8px;
      }

