      /* CSS cho các tab */
      .tab {
        overflow: hidden;
      }

      /* CSS cho các nút bên trong các tab */
      .tab button {
        background-color: white;
        color: #212b36;
        float: left;
        border-radius: 10px;
        outline: none;
        cursor: pointer;
        padding: 14px 16px;
        transition: 0.3s;
      }

      /* Thay đổi màu nền của các nút khi hover */
      .tab button:hover {
        background-color: #5caf90;
        color: white;
      }

      /* Tạo màu nền cho nút hoạt động */
      .tab button.active {
        background-color: #5caf90;
        color: white;
      }

      /* CSS cho nội dung tab */
      .tabcontent {
        display: none;
      }

      /* star rating */

      .rating-container {
        display: flex;
