let news = [];
let recent_news = [];
const apiBlogList =
  "/api/method/go1_cms.api.website.blog.get_blog_list";
const apiRecentNews =
  "/api/method/go1_cms.api.website.blog.get_recent_blog";
const apiOptionsList =
  "/api/method/go1_cms.api.website.blog.get_categories";
const name_section = "{{ name_section}}";
let text_search = "";
let itemsPerPage = 9; // Số lượng công việc trên mỗi trang
let total_page = 1;
const newSearch = document.getElementById("news-search");
const category_search = document.getElementById("category-select");

// Lấy URL hiện tại
const currentUrl = new URL(window.location.href);

// Lấy số trang hiện tại từ URL hoặc mặc định là 1
let currentPage = parseInt(currentUrl.searchParams.get("page_number")) || 1;

const searchParams = new URLSearchParams(window.location.search);
const text_search_current = searchParams.get("search");
const category_search_current = searchParams.get("category");

if (text_search_current) {
  newSearch.value = text_search_current;
  text_search = text_search_current;
}

const getOptionsList = () => {
  fetch(apiOptionsList, {
    method: "GET",
  })
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.message) {
        const options = result.message?.data;
        populateSelect(options);
        if (category_search_current) {
          category_search.value = category_search_current;
        }
      }
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Options fetched");
      getBlogList();
    });
};

function populateSelect(options) {
  // Xóa các tùy chọn cũ, nếu có
  options.forEach((option) => {
    const optionElement = document.createElement("option");
    optionElement.value = option.value; // Assuming your options have a 'value' property
    optionElement.text = option.label; // Assuming your options have a 'label' property
    category_search.appendChild(optionElement);
  });
}

const getRecentBlog = () => {
  fetch(`${apiRecentNews}?name_section=${name_section}`, {
    method: "GET",
  })
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.message) {
        recent_news = result.message?.data;
      }
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Call api recent news");
      displayBlogRecent();
    });
};

const getBlogList = () => {
  fetch(
    `${apiBlogList}?name_section=${name_section}&text_search=${text_search}&page_no=${currentPage}&blog_category=${category_search.value}`,
    {
      method: "GET",
    }
  )
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.message) {
        news = result.message?.data;
        itemsPerPage = result.message?.pagination.limit;
        total_page = result.message?.pagination.total_page;
      }
    })
    .catch((error) => {
      console.log(error);
    })
    .finally(() => {
      console.log("Call API list new");
      displayNews();
      updateURL();
    });
};

function displayNews() {
  const newContainer = document.getElementById("news-container");
  newContainer.innerHTML = ""; // Xóa nội dung cũ
  if (news.length === 0) {
    const emptyElement = document.createElement("div");
    emptyElement.className =
      "flex flex-col justify-center items-center gap-[38px] py-[80px] col-span-full mt-[38px]";
    emptyElement.innerHTML = `
      <img src="/files/empty-state.svg" alt="" class="w-[245.64px] h-[236.85px]"></img>
      <span class="text-[#212B36] font-bold text-xl">Không có kết quả tìm kiếm </span>
      `;
    newContainer.appendChild(emptyElement);
  } else {
    for (const blog of news) {
      const blogElement = document.createElement("a");
      blogElement.href = blog.route;
      blogElement.className =
        "w-full h-full item bg-white border border-gray-200 rounded-lg shadow";
      blogElement.innerHTML = `
                <img
                    class="rounded-t-lg w-full h-[147px]"
                    src='${blog.meta_image}'
                    alt=""
                />

                    <div class="p-5">
                        <p class="text-gray-500 font-normal text-sm leading-[21px]">
                          <i class="fa fa-clock-o"></i> ${blog.published_on}
                        </p>

                        <a href="#">
                          <h5
                            class="mb-2 text-lg font-bold tracking-tight text-[#212B36]"
                          >
                            ${blog.title}
                          </h5>
                        </a>
                        <p
                          class="mb-3 font-normal text-sm text-[#637381] dark:text-gray-400 text-container"
                        >
                          ${blog.blog_intro}
                        </p>
                    </div>
            `;
      newContainer.appendChild(blogElement);
    }
    displayPagination();
    window.scrollTo(0, 400);
  }
}

function displayBlogRecent() {
  const blogRecent = document.getElementById("blog-recent-container");

  for (const blog of recent_news) {
    const blogElement = document.createElement("a");
    blogElement.href = blog.route;
    blogElement.className = "h-full flex flex-row item-center w-full my-6";
    blogElement.innerHTML = `
          <img
            class="h-[50px] w-[63px] rounded-lg"
            alt=""
            src='${blog.meta_image}'
            />
            <div class="mx-3">
                <p
                    class="text-[#637381] font-normal text-xs leading-[18px]"
                >
                    <i class="fa fa-clock-o"></i> ${blog.published_on}
                </p>
                <span
                    class="text-[#212B36] font-semibold text-base leading-5"
                >${blog.title}
                </span>
            </div>
        `;
    blogRecent.appendChild(blogElement);
  }

  // Hiển thị các nút phân trang
  displayPagination();
  window.scrollTo(0, 400);
}

function displayPagination() {
  const paginationContainer = document.getElementById("pagination-container");
  total_page === 1
    ? paginationContainer.classList.add("hidden")
    : paginationContainer.classList.remove("hidden");

  const prevPage = document.getElementById("prev-page");
  const nextPage = document.getElementById("next-page");

  currentPage === 1
    ? prevPage?.classList.add("no-active")
    : prevPage.classList.remove("no-active");
  currentPage === total_page
    ? nextPage?.classList.add("no-active")
    : nextPage.classList.remove("no-active");

  paginationContainer
    ?.querySelectorAll(".page-number, .pagination-ellipsis")
    ?.forEach((button) => button.remove());

  let startPage = Math.max(1, currentPage - 1);
  let endPage = Math.min(total_page, currentPage + 1);

  // Điều chỉnh để luôn hiển thị tối đa 5 trang
  if (endPage - startPage < 4) {
    if (startPage === 1) {
      endPage = Math.min(total_page, startPage + 4);
    } else if (endPage === total_page) {
      startPage = Math.max(1, endPage - 4);
    }
  }

  // Thêm nút "1" và "..." nếu cần
  if (startPage > 1) {
    createPageButton(1, paginationContainer); // Nút đầu tiên

    if (startPage > 2) {
      createEllipsis(paginationContainer); // Thêm dấu "..."
    }
  }

  // Tạo các nút trang
  for (let i = startPage; i <= endPage; i++) {
    createPageButton(i, paginationContainer, i === currentPage);
  }

  // Thêm nút "..." và nút cuối cùng nếu cần
  if (endPage < total_page) {
    if (endPage < total_page - 1) {
      createEllipsis(paginationContainer); // Thêm dấu "..."
    }
    createPageButton(total_page, paginationContainer); // Nút cuối cùng
  }
}

// Hàm tạo nút trang
function createPageButton(page, container, isActive = false) {
  const pageButton = document.createElement("button");
  pageButton.innerText = page;
  pageButton.className = `page-number text-sm text-[#222222] font-normal px-2 py-[1px] ${
    isActive ? "active" : ""
  }`;
  pageButton.addEventListener("click", () => {
    currentPage = page;
    getBlogList();
    updateURL(); // Cập nhật URL
  });
  container.insertBefore(pageButton, document.getElementById("next-page"));
}

// Hàm tạo dấu "..."
function createEllipsis(container) {
  const ellipsis = document.createElement("span");
  ellipsis.innerText = "...";
  ellipsis.className = "pagination-ellipsis px-2 py-[1px]";
  container.insertBefore(ellipsis, document.getElementById("next-page"));
}

function updateURL() {
  const currentUrl = new URL(window.location.href);
  currentUrl.searchParams.set("page_number", currentPage);
  currentUrl.searchParams.set("search", newSearch.value);
  currentUrl.searchParams.set("category", category_search.value);
  history.replaceState({}, "", currentUrl);
}

// Thêm sự kiện click cho nút Previous
document.getElementById("prev-page").addEventListener("click", () => {
  if (currentPage > 1) {
    currentPage--;
    getBlogList();
  }
});

// Thêm sự kiện click cho nút Next
document.getElementById("next-page").addEventListener("click", () => {
  if (currentPage < total_page) {
    currentPage++;
    getBlogList();
  }
});

// Gọi hàm hiển thị lần đầu tiên
getRecentBlog();
getOptionsList();
setTimeout(() => {
  getBlogList();
}, 0);

// Debounce function: kỹ thuật debounced
function debounce(func, wait) {
  let timeout;
  return function (...args) {
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(this, args), wait);
  };
}

newSearch.addEventListener(
  "input",
  debounce(() => {
    text_search = newSearch.value;
    currentPage = 1;
    getBlogList();
  }, 1000)
);

category_search.addEventListener("change", () => {
  getBlogList();
});

